      ...patient,
      medical_notes: patient.medical_notes || '',
      prescriptions: patient.prescriptions || '',
      last_checkup: patient.last_checkup || new Date().toISOString().split('T')[0],
      next_appointment: patient.next_appointment || ''
    });

