      'father': 'ðŸ‘¨',
      'spouse': 'ðŸ’‘',
      'brother': 'ðŸ‘¦',
      'sister': 'ðŸ‘§',
      'grandfather': 'ðŸ‘´',
      'grandmother': 'ðŸ‘µ',
      'other': 'ðŸ‘¥'
