import { useEffect, useState } from 'react';

export default function PatientDashboard() {
  const [patients, setPatients] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [disease, setDisease] = useState('');
  const [editingPatientId, setEditingPatientId] = useState(null);
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetch('http://localhost:8080/api/patients', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setPatients(data));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (typeof name !== 'string' || typeof age !== 'string') {
      alert("Name and Age must be valid strings.");
      return;
    }

    if (!name.trim() || !age.trim()) {
      alert("Name and Age are required!");
      return;
    }

    const patientData = {
      name: name.trim(),
      age: parseInt(age),
      disease: disease?.trim() || '',
    };

    try {
      if (editingPatientId) {
        // PUT - Update Patient
        const res = await fetch(`http://localhost:8080/api/patients/${editingPatientId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(patientData),
        });

        if (res.ok) {
  alert("✅ Patient updated");

  // 🔁 Re-fetch patients
  const refreshed = await fetch('http://localhost:8080/api/patients', {
    headers: { Authorization: `Bearer ${token}` },
  });
  const updatedList = await refreshed.json();
  setPatients(updatedList);
}
 else {
          alert('❌ Failed to update patient');
        }
      } else {
        // POST - Add Patient
        const res = await fetch('http://localhost:8080/api/patients', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(patientData),
        });

        if (res.ok) {
          const newPatient = await res.json();
          setPatients([...patients, newPatient]);
        } else {
          alert('❌ Failed to add patient');
        }
      }
    } catch (error) {
      alert('❌ Network error');
    }

    // Reset form
    setShowForm(false);
    setEditingPatientId(null);
    setName('');
    setAge('');
    setDisease('');
  };

  const handleEdit = (patient) => {
    setEditingPatientId(patient.id);
    setName(patient.name || '');
    setAge(String(patient.age) || '');
    setDisease(patient.disease || '');
    setShowForm(true);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-2xl font-bold mb-4">Patient Dashboard</h1>

      <button
        className="mb-4 px-4 py-2 bg-blue-500 text-white rounded"
        onClick={() => {
          setShowForm(!showForm);
          setEditingPatientId(null);
          setName('');
          setAge('');
          setDisease('');
        }}
      >
        {showForm ? 'Cancel' : 'Add Patient'}
      </button>

      {showForm && (
        <form onSubmit={handleSubmit} className="bg-white p-4 rounded shadow mb-4">
          <input
            type="text"
            placeholder="Name"
            className="block w-full mb-2 p-2 border rounded"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            type="number"
            placeholder="Age"
            className="block w-full mb-2 p-2 border rounded"
            value={age}
            onChange={(e) => setAge(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Disease"
            className="block w-full mb-2 p-2 border rounded"
            value={disease}
            onChange={(e) => setDisease(e.target.value)}
          />
          <button
            type="submit"
            className="px-4 py-2 bg-green-500 text-white rounded"
          >
            {editingPatientId ? 'Update' : 'Submit'}
          </button>
        </form>
      )}

      <div className="bg-white shadow p-4 rounded">
        {patients.length === 0 ? (
          <p>No patients found.</p>
        ) : (
          <ul>
            {patients.map((p) => (
              <li key={p.id} className="border-b py-2 flex justify-between items-center">
                <span>
                  <strong>{p.name}</strong> - Age: {p.age}, Disease: {p.disease || 'N/A'}
                </span>
                <button
                  onClick={() => handleEdit(p)}
                  className="ml-4 px-2 py-1 bg-yellow-500 text-white rounded"
                >
                  Edit
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
