import { useState, useEffect } from 'react';

export default function DoctorDashboard() {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editingPatient, setEditingPatient] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [patientHistory, setPatientHistory] = useState([]);
  const [patientRecords, setPatientRecords] = useState([]);
  const [isReturningPatient, setIsReturningPatient] = useState(false);
  const [totalVisits, setTotalVisits] = useState(0);
  const [saving, setSaving] = useState(false);
  const [loadingHistory, setLoadingHistory] = useState(false);
  const token = localStorage.getItem('token');

  // Fetch patients
  const fetchPatients = async () => {
    try {
      setLoading(true);
      console.log('Fetching patients...');
      const response = await fetch('http://localhost:8080/api/patients', {
        headers: { 
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log('Patients fetched:', data);
      setPatients(data || []);
      setError('');
    } catch (err) {
      setError('Failed to load patients');
      console.error('Error fetching patients:', err);
    } finally {
      setLoading(false);
    }
  };

  // Fetch patient history by phone number
  const fetchPatientHistoryByPhone = async (phoneNumber) => {
    try {
      setLoadingHistory(true);
      console.log(`Fetching complete history for phone ${phoneNumber}...`);
      const response = await fetch(`http://localhost:8080/api/patients/phone/${phoneNumber}/history`, {
        headers: { 
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Complete patient history:', data);
        setPatientHistory(data.medical_history || []);
        setPatientRecords(data.patient_records || []);
        setIsReturningPatient(data.is_returning || false);
        setTotalVisits(data.total_visits || 0);
      } else {
        console.log('No history found or error fetching history');
        setPatientHistory([]);
        setPatientRecords([]);
        setIsReturningPatient(false);
        setTotalVisits(0);
      }
    } catch (err) {
      console.error('Failed to fetch patient history:', err);
      setPatientHistory([]);
      setPatientRecords([]);
      setIsReturningPatient(false);
      setTotalVisits(0);
    } finally {
      setLoadingHistory(false);
    }
  };

  // Update patient
  const updatePatient = async (e) => {
    e.preventDefault();
    setSaving(true);

    try {
      console.log('Updating patient:', editingPatient);

      const response = await fetch(`http://localhost:8080/api/patients/${editingPatient.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({
          name: editingPatient.name,
          age: editingPatient.age,
          gender: editingPatient.gender,
          phone_number: editingPatient.phone_number,
          diagnosis: editingPatient.diagnosis || '',
          medical_notes: editingPatient.medical_notes || '',
          prescriptions: editingPatient.prescriptions || '',
          last_checkup: editingPatient.last_checkup || '',
          next_appointment: editingPatient.next_appointment || ''
        }),
      });

      if (!response.ok) {
        const errorData = await response.text();
        throw new Error(`Failed to update patient: ${errorData}`);
      }

      const result = await response.json();
      console.log('Update result:', result);

      // Refresh patient list and history
      await fetchPatients();
      await fetchPatientHistoryByPhone(editingPatient.phone_number);

      alert('âœ… Patient medical record updated successfully!');
    } catch (err) {
      alert(`âŒ Error updating patient: ${err.message}`);
      console.error('Update error:', err);
    } finally {
      setSaving(false);
    }
  };

  // Open edit modal
  const handleEdit = async (patient) => {
    console.log('Editing patient:', patient);
    setEditingPatient({
      ...patient,
      medical_notes: patient.medical_notes || '',
      prescriptions: patient.prescriptions || '',
      last_checkup: patient.last_checkup || new Date().toISOString().split('T')[0],
      next_appointment: patient.next_appointment || ''
    });

    // Fetch complete history by phone number
    await fetchPatientHistoryByPhone(patient.phone_number);
    setShowEditModal(true);
  };

  // Close edit modal
  const handleCloseModal = () => {
    setShowEditModal(false);
    setEditingPatient(null);
    setPatientHistory([]);
    setPatientRecords([]);
    setIsReturningPatient(false);
    setTotalVisits(0);
  };

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditingPatient({
      ...editingPatient,
      [name]: value,
    });
  };

  useEffect(() => {
    fetchPatients();
  }, []);

  return (
    <div className="content">
      <div className="content-card">
        <div className="dashboard-header">
          <h1 className="dashboard-title">ðŸ‘¨â€âš•ï¸ Doctor Dashboard</h1>
          <p className="dashboard-subtitle">Review and update patient medical records</p>
        </div>

        {/* Enhanced Stats cards */}
        <div className="stats-grid">
          <div className="stat-card green">
            <div className="stat-info">
              <h3>Total Patients</h3>
              <p>{patients.length}</p>
            </div>
            <div className="stat-icon">ðŸ‘¥</div>
          </div>

          <div className="stat-card blue">
            <div className="stat-info">
              <h3>Active Cases</h3>
              <p>{patients.filter(p => p.diagnosis && p.diagnosis !== 'None specified' && p.diagnosis !== '').length}</p>
            </div>
            <div className="stat-icon">ðŸ“‹</div>
          </div>

          <div className="stat-card purple">
            <div className="stat-info">
              <h3>Returning Patients</h3>
              <p>{patients.filter(p => p.medical_history && p.medical_history.length > 0).length}</p>
            </div>
            <div className="stat-icon">ðŸ”„</div>
          </div>

          <div className="stat-card orange">
            <div className="stat-info">
              <h3>Today's Updates</h3>
              <p>{patients.filter(p => p.updated_at && new Date(p.updated_at).toDateString() === new Date().toDateString()).length}</p>
            </div>
            <div className="stat-icon">ðŸ©º</div>
          </div>
        </div>

        {/* Patient Records */}
        <div style={{ marginTop: '40px' }}>
          <h2 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '16px' }}>
            ðŸ“‹ Patient Records
          </h2>
          <p style={{ color: '#6b7280', marginBottom: '24px' }}>
            Click on any patient to view their complete medical history and update current condition
          </p>

          {loading && (
            <div style={{ textAlign: 'center', padding: '40px' }}>
              <p>ðŸ”„ Loading patients...</p>
            </div>
          )}

          {error && (
            <div style={{ textAlign: 'center', padding: '40px' }}>
              <p className="error-message">âŒ {error}</p>
              <button 
                onClick={fetchPatients}
                style={{ 
                  marginTop: '10px', 
                  padding: '8px 16px', 
                  backgroundColor: '#3b82f6', 
                  color: 'white', 
                  border: 'none', 
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                ðŸ”„ Retry
              </button>
            </div>
          )}

          {!loading && !error && (
            <>
              {patients.length === 0 ? (
                <div style={{ textAlign: 'center', padding: '40px' }}>
                  <p>ðŸ“­ No patients found.</p>
                </div>
              ) : (
                <div className="patient-grid">
                  {patients.map((patient) => {
                    const hasHistory = patient.medical_history && patient.medical_history.length > 0;
                    return (
                      <div key={patient.id} className={`patient-card ${hasHistory ? 'returning-patient' : 'new-patient'}`}>
                        <div className="patient-header">
                          <h3 className="patient-name">{patient.name}</h3>
                          <div className="patient-badges">
                            <span className="patient-id">ID: {patient.id}</span>
                            {hasHistory && <span className="returning-badge">ðŸ”„ Returning</span>}
                          </div>
                        </div>

                        <div className="patient-details">
                          <div className="patient-detail">
                            ðŸ‘¤ Age: {patient.age} years | Gender: {patient.gender}
                          </div>
                          <div className="patient-detail">
                            ðŸ“ž Phone: {patient.phone_number}
                          </div>
                          <div className="patient-detail">
                            ðŸ¥ Current Diagnosis: {patient.diagnosis || 'None specified'}
                          </div>
                          {patient.prescriptions && (
                            <div className="patient-detail">
                              ðŸ’Š Current Medications: {patient.prescriptions}
                            </div>
                          )}
                          {patient.last_checkup && (
                            <div className="patient-detail">
                              ðŸ—“ï¸ Last Checkup: {patient.last_checkup}
                            </div>
                          )}
                          {hasHistory && (
                            <div className="patient-detail history-indicator">
                              ðŸ“š Previous Visits: {patient.medical_history.length}
                            </div>
                          )}
                          {patient.updated_at && (
                            <div className="patient-detail">
                              â° Last Updated: {new Date(patient.updated_at).toLocaleDateString()}
                            </div>
                          )}
                        </div>

                        <div className="patient-actions">
                          <button 
                            className="btn btn-edit"
                            onClick={() => handleEdit(patient)}
                          >
                            {hasHistory ? 'ðŸ“š View Complete History & Update' : 'âœï¸ Add First Medical Record'}
                          </button>
                        </div>
                      </div>
                    );
                  })}
                </div>
              )}
            </>
          )}
        </div>

        {/* Enhanced Edit Modal with Complete History */}
        {showEditModal && editingPatient && (
          <div className="modal-overlay">
            <div className="modal-content enhanced-modal">
              <div className="modal-header">
                <div className="patient-info-header">
                  <h2 className="modal-title">ðŸ©º Medical Record - {editingPatient.name}</h2>
                  <div className="patient-status">
                    {isReturningPatient ? (
                      <span className="status-badge returning">
                        ðŸ”„ Returning Patient ({totalVisits} previous visits)
                      </span>
                    ) : (
                      <span className="status-badge new">
                        âœ¨ New Patient (First Visit)
                      </span>
                    )}
                  </div>
                </div>
                <p className="modal-subtitle">
                  ðŸ“ž Phone: {editingPatient.phone_number} | ðŸ‘¤ Age: {editingPatient.age} | Gender: {editingPatient.gender}
                </p>
                <button 
                  className="modal-close"
                  onClick={handleCloseModal}
                  style={{
                    position: 'absolute',
                    top: '16px',
                    right: '16px',
                    background: 'none',
                    border: 'none',
                    fontSize: '24px',
                    cursor: 'pointer',
                    color: '#6b7280'
                  }}
                >
                  âœ•
                </button>
              </div>

              <div className="modal-body">
                {/* Complete Medical History Section */}
                <div className="history-section">
                  <h3 className="section-title">
                    ðŸ“š Complete Medical History 
                    {isReturningPatient && <span className="visit-count">({totalVisits} visits)</span>}
                  </h3>

                  {loadingHistory ? (
                    <div className="loading-history">ðŸ”„ Loading complete history...</div>
                  ) : (
                    <>
                      {/* Previous Patient Records */}
                      {patientRecords.length > 1 && (
                        <div className="previous-records">
                          <h4 className="subsection-title">ðŸ‘¥ Previous Registrations</h4>
                          <div className="records-list">
                            {patientRecords.map((record, index) => (
                              <div key={record.id} className={`record-item ${record.id === editingPatient.id ? 'current-record' : ''}`}>
                                <span className="record-info">
                                  {record.id === editingPatient.id ? 'ðŸ“ Current: ' : 'ðŸ“‹ Previous: '}
                                  ID {record.id} - Registered {new Date(record.created_at).toLocaleDateString()}
                                </span>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}

                      {/* Medical History */}
                      {patientHistory.length > 0 ? (
                        <div className="history-list">
                          <h4 className="subsection-title">ðŸ©º Medical Visit History</h4>
                          {patientHistory.map((history, index) => (
                            <div key={history.id || index} className="history-item">
                              <div className="history-header">
                                <span className="visit-date">
                                  ðŸ—“ï¸ {new Date(history.visit_date).toLocaleDateString()} 
                                  {new Date(history.visit_date).toDateString() === new Date().toDateString() && 
                                    <span className="today-badge">Today</span>
                                  }
                                </span>
                                <span className="doctor-name">
                                  ðŸ‘¨â€âš•ï¸ {history.doctor_name}
                                </span>
                              </div>
                              <div className="history-details">
                                {history.diagnosis && (
                                  <p><strong>ðŸ¥ Diagnosis:</strong> {history.diagnosis}</p>
                                )}
                                {history.medical_notes && (
                                  <p><strong>ðŸ“ Notes:</strong> {history.medical_notes}</p>
                                )}
                                {history.prescriptions && (
                                  <p><strong>ðŸ’Š Prescriptions:</strong> {history.prescriptions}</p>
                                )}
                              </div>
                            </div>
                          ))}
                        </div>
                      ) : (
                        <div className="no-history">
                          <div className="no-history-icon">ðŸ“</div>
                          <p>No previous medical history found.</p>
                          <p>This will be the first medical record for this phone number.</p>
                        </div>
                      )}
                    </>
                  )}
                </div>

                {/* Current Update Form */}
                <div className="update-section">
                  <h3 className="section-title">
                    ðŸ©º {isReturningPatient ? 'Current Visit Update' : 'First Medical Record'}
                  </h3>
                  <form onSubmit={updatePatient}>
                    <div className="form-group">
                      <label className="form-label">
                        ðŸ¥ Current Diagnosis *
                        {isReturningPatient && <span className="helper-text">Update based on current symptoms</span>}
                      </label>
                      <textarea
                        name="diagnosis"
                        className="form-input"
                        value={editingPatient.diagnosis || ''}
                        onChange={handleInputChange}
                        placeholder="Enter current diagnosis or condition"
                        rows="3"
                        required
                      />
                    </div>

                    <div className="form-group">
                      <label className="form-label">
                        ðŸ“ Medical Notes
                        <span className="helper-text">Detailed observations, symptoms, examination findings</span>
                      </label>
                      <textarea
                        name="medical_notes"
                        className="form-input"
                        value={editingPatient.medical_notes || ''}
                        onChange={handleInputChange}
                        placeholder="Enter detailed medical notes, observations, symptoms, examination findings..."
                        rows="4"
                      />
                    </div>

                    <div className="form-group">
                      <label className="form-label">
                        ðŸ’Š Prescriptions
                        <span className="helper-text">Medications with dosage and frequency</span>
                      </label>
                      <textarea
                        name="prescriptions"
                        className="form-input"
                        value={editingPatient.prescriptions || ''}
                        onChange={handleInputChange}
                        placeholder="e.g., Paracetamol 500mg - 2 times daily after meals&#10;Amoxicillin 250mg - 3 times daily for 7 days"
                        rows="3"
                      />
                    </div>

                    <div className="form-row">
                      <div className="form-group">
                        <label className="form-label">ðŸ“… Checkup Date</label>
                        <input
                          type="date"
                          name="last_checkup"
                          className="form-input"
                          value={editingPatient.last_checkup || ''}
                          onChange={handleInputChange}
                        />
                      </div>

                      <div className="form-group">
                        <label className="form-label">ðŸ—“ï¸ Next Appointment</label>
                        <input
                          type="date"
                          name="next_appointment"
                          className="form-input"
                          value={editingPatient.next_appointment || ''}
                          onChange={handleInputChange}
                          min={new Date().toISOString().split('T')[0]}
                        />
                      </div>
                    </div>

                    <div className="form-actions">
                      <button 
                        type="button" 
                        className="btn btn-cancel"
                        onClick={handleCloseModal}
                        disabled={saving}
                      >
                        Cancel
                      </button>
                      <button 
                        type="submit" 
                        className="btn btn-submit"
                        disabled={saving}
                      >
                        {saving ? 'ðŸ’¾ Saving...' : 'ðŸ’¾ Save Medical Record'}
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Enhanced Styles */}
      <style jsx>{`
        .enhanced-modal .modal-content {
          max-width: 1200px;
          max-height: 95vh;
          overflow-y: auto;
          position: relative;
        }

        .patient-info-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 8px;
        }

        .patient-status {
          display: flex;
          gap: 8px;
        }

        .status-badge {
          padding: 4px 12px;
          border-radius: 20px;
          font-size: 12px;
          font-weight: 600;
        }

        .status-badge.returning {
          background: #dbeafe;
          color: #1e40af;
        }

        .status-badge.new {
          background: #dcfce7;
          color: #166534;
        }

        .returning-patient {
          border-left: 4px solid #3b82f6;
        }

        .new-patient {
          border-left: 4px solid #10b981;
        }

        .patient-badges {
          display: flex;
          flex-direction: column;
          align-items: flex-end;
          gap: 4px;
        }

        .returning-badge {
          background: #dbeafe;
          color: #1e40af;
          padding: 2px 8px;
          border-radius: 12px;
          font-size: 11px;
          font-weight: 600;
        }

        .history-indicator {
          background: #f0f9ff;
          color: #0369a1;
          padding: 4px 8px;
          border-radius: 4px;
          font-weight: 500;
        }

        .modal-body {
          display: flex;
          gap: 30px;
          padding: 20px;
        }

        .history-section {
          flex: 1.2;
          border-right: 1px solid #e5e7eb;
          padding-right: 20px;
        }

        .update-section {
          flex: 1;
          padding-left: 20px;
        }

        .section-title {
          font-size: 18px;
          font-weight: bold;
          margin-bottom: 16px;
          color: #374151;
          display: flex;
          align-items: center;
          gap: 8px;
        }

        .visit-count {
          font-size: 14px;
          color: #6b7280;
          font-weight: normal;
        }

        .subsection-title {
          font-size: 16px;
          font-weight: 600;
          margin: 16px 0 8px 0;
          color: #4b5563;
        }

        .loading-history {
          text-align: center;
          padding: 40px;
          color: #6b7280;
        }

        .previous-records {
          margin-bottom: 20px;
        }

        .records-list {
          background: #f9fafb;
          border-radius: 8px;
          padding: 12px;
        }

        .record-item {
          padding: 8px;
          margin: 4px 0;
          border-radius: 4px;
        }

        .current-record {
          background: #dbeafe;
          color: #1e40af;
          font-weight: 500;
        }

        .history-list {
          max-height: 500px;
          overflow-y: auto;
        }

        .history-item {
          background: #f9fafb;
          border: 1px solid #e5e7eb;
          border-radius: 8px;
          padding: 16px;
          margin-bottom: 12px;
        }

        .history-header {
          display: flex;
          justify-content: space-between;
          margin-bottom: 8px;
          font-size: 14px;
          color: #6b7280;
        }

        .today-badge {
          background: #fef3c7;
          color: #92400e;
          padding: 2px 6px;
          border-radius: 8px;
          font-size: 10px;
          margin-left: 8px;
        }

        .history-details p {
          margin: 6px 0;
          font-size: 14px;
          line-height: 1.4;
        }

        .no-history {
          text-align: center;
          padding: 40px;
          background: #f9fafb;
          border-radius: 8px;
          border: 2px dashed #d1d5db;
        }

        .no-history-icon {
          font-size: 48px;
          margin-bottom: 16px;
        }

        .no-history p {
          color: #6b7280;
          margin: 8px 0;
        }

        .form-row {
          display: flex;
          gap: 16px;
        }

        .form-row .form-group {
          flex: 1;
        }

        .form-group {
          margin-bottom: 16px;
        }

        .form-label {
          display: block;
          margin-bottom: 6px;
          font-weight: 600;
          color: #374151;
          font-size: 14px;
        }

        .helper-text {
          display: block;
          font-size: 12px;
          color: #6b7280;
          font-weight: normal;
          margin-top: 2px;
        }

        .form-input {
          width: 100%;
          padding: 10px 12px;
          border: 1px solid #d1d5db;
          border-radius: 6px;
          font-size: 14px;
          transition: border-color 0.2s;
        }

        .form-input:focus {
          outline: none;
          border-color: #3b82f6;
          box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
        }

        .form-actions {
          display: flex;
          gap: 12px;
          justify-content: flex-end;
          margin-top: 24px;
          padding-top: 20px;
          border-top: 1px solid #e5e7eb;
        }

        .btn {
          padding: 12px 24px;
          border: none;
          border-radius: 6px;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.2s;
          font-size: 14px;
        }

        .btn:disabled {
          opacity: 0.6;
          cursor: not-allowed;
        }

        .btn-cancel {
          background: #f3f4f6;
          color: #374151;
        }

        .btn-cancel:hover:not(:disabled) {
          background: #e5e7eb;
        }

        .btn-submit {
          background: #3b82f6;
          color: white;
        }

        .btn-submit:hover:not(:disabled) {
          background: #2563eb;
        }

        .stats-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
          gap: 20px;
          margin-bottom: 30px;
        }

        .stat-card.orange {
          background: linear-gradient(135deg, #fed7aa 0%, #fb923c 100%);
          color: white;
        }
      `}</style>
    </div>
  );
}

