"use client"

import { useState } from "react"

const Login = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    role: "receptionist",
  })
  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)

  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)
    setError("")

    try {
      // üîß FIXED: Changed from /api/login to /auth/login
      const response = await fetch("http://localhost:8080/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: formData.email,
          password: formData.password,
        }),
      })

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`)
      }

      const data = await response.json()

      // ‚úÖ Validate role matches
      if (data.user.role !== formData.role) {
        setError(
          `‚ùå Access denied. You are registered as a ${data.user.role}, but trying to login as ${formData.role}.`,
        )
        setLoading(false)
        return
      }

      // Store token and redirect
      localStorage.setItem("token", data.token)
      localStorage.setItem("user", JSON.stringify(data.user))

      if (data.user.role === "doctor") {
        window.location.href = "/doctor"
      } else if (data.user.role === "receptionist") {
        window.location.href = "/receptionist"
      }
    } catch (error) {
      console.error("Login error:", error)
      setError("üö® Login failed. Please check your credentials.")
    }

    setLoading(false)
  }

  return (
    <div className="login-container">
      <div className="login-card">
        {/* Header */}
        <div className="login-header">
          <div className="hospital-icon">
            <span>üè•</span>
          </div>
          <h2 className="login-title">Hospital Portal</h2>
          <p className="login-subtitle">Sign in to your account</p>
        </div>

        {/* Error Message */}
        {error && (
          <div className="error-message">
            <p>{error}</p>
          </div>
        )}

        {/* Form */}
        <form onSubmit={handleSubmit} className="login-form">
          {/* Email Field */}
          <div className="form-group">
            <label htmlFor="email" className="form-label">
              üìß Email Address
            </label>
            <input
              id="email"
              type="email"
              required
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              className="form-input"
              placeholder="Enter your email"
            />
          </div>

          {/* Password Field */}
          <div className="form-group">
            <label htmlFor="password" className="form-label">
              üîí Password
            </label>
            <input
              id="password"
              type="password"
              required
              value={formData.password}
              onChange={(e) => setFormData({ ...formData, password: e.target.value })}
              className="form-input"
              placeholder="Enter your password"
            />
          </div>

          {/* Role Selection */}
          <div className="form-group">
            <label htmlFor="role" className="form-label">
              üë§ Login as
            </label>
            <select
              id="role"
              value={formData.role}
              onChange={(e) => setFormData({ ...formData, role: e.target.value })}
              className="form-input"
            >
              <option value="receptionist">üë©‚Äçüíº Receptionist</option>
              <option value="doctor">üë®‚Äç‚öïÔ∏è Doctor</option>
            </select>
          </div>

          {/* Submit Button */}
          <button type="submit" disabled={loading} className="submit-button">
            {loading ? (
              <span className="loading-content">
                <span className="spinner"></span>
                Signing in...
              </span>
            ) : (
              "üöÄ Sign In"
            )}
          </button>
        </form>

        {/* Footer */}
        <div className="login-footer">
          <p>
            Server: <span className="server-url">http://localhost:8080</span>
          </p>
        </div>
      </div>

      {/* CSS Styles */}
      <style jsx>{`
        .login-container {
          min-height: 100vh;
          display: flex;
          align-items: center;
          justify-content: center;
          background: linear-gradient(135deg, #e0f2fe 0%, #e1e5fe 50%, #f3e5f5 100%);
          padding: 20px;
        }

        .login-card {
          max-width: 400px;
          width: 100%;
          background: white;
          border-radius: 16px;
          box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
          padding: 32px;
          border: 1px solid #f3f4f6;
        }

        .login-header {
          text-align: center;
          margin-bottom: 32px;
        }

        .hospital-icon {
          margin: 0 auto 16px auto;
          width: 64px;
          height: 64px;
          background: #dbeafe;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
        }

        .hospital-icon span {
          font-size: 32px;
        }

        .login-title {
          font-size: 30px;
          font-weight: bold;
          color: #111827;
          margin: 0 0 8px 0;
        }

        .login-subtitle {
          color: #6b7280;
          margin: 0;
        }

        .error-message {
          margin-bottom: 24px;
          padding: 16px;
          background: #fef2f2;
          border: 1px solid #fecaca;
          border-radius: 8px;
        }

        .error-message p {
          color: #b91c1c;
          font-size: 14px;
          margin: 0;
        }

        .login-form {
          display: flex;
          flex-direction: column;
          gap: 24px;
        }

        .form-group {
          display: flex;
          flex-direction: column;
        }

        .form-label {
          display: block;
          font-size: 14px;
          font-weight: 500;
          color: #374151;
          margin-bottom: 8px;
        }

        .form-input {
          width: 100%;
          padding: 12px 16px;
          border: 1px solid #d1d5db;
          border-radius: 8px;
          font-size: 16px;
          transition: all 0.2s;
          box-sizing: border-box;
        }

        .form-input:focus {
          outline: none;
          border-color: #3b82f6;
          box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
        }

        .submit-button {
          width: 100%;
          background: #2563eb;
          color: white;
          font-weight: 500;
          padding: 12px 16px;
          border: none;
          border-radius: 8px;
          font-size: 16px;
          cursor: pointer;
          transition: all 0.2s;
        }

        .submit-button:hover:not(:disabled) {
          background: #1d4ed8;
        }

        .submit-button:disabled {
          background: #93c5fd;
          cursor: not-allowed;
        }

        .submit-button:focus {
          outline: none;
          box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.5);
        }

        .loading-content {
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 8px;
        }

        .spinner {
          width: 20px;
          height: 20px;
          border: 2px solid #ffffff;
          border-top: 2px solid transparent;
          border-radius: 50%;
          animation: spin 1s linear infinite;
        }

        @keyframes spin {
          0% {
            transform: rotate(0deg);
          }
          100% {
            transform: rotate(360deg);
          }
        }

        .login-footer {
          margin-top: 24px;
          text-align: center;
        }

        .login-footer p {
          font-size: 12px;
          color: #6b7280;
          margin: 0;
        }

        .server-url {
          font-family: monospace;
          color: #2563eb;
        }
      `}</style>
    </div>
  )
}

export default Login
