import { useEffect, useState } from 'react';

export default function PatientDashboard() {
  const [patients, setPatients] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [disease, setDisease] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editData, setEditData] = useState({ name: '', age: '', disease: '' });
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetch('http://localhost:8080/api/patients', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setPatients(data));
  }, [token]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name.trim() || !age.trim()) {
      alert('Name and Age are required!');
      return;
    }

    const res = await fetch('http://localhost:8080/api/patients', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        name: name.trim(),
        age: parseInt(age),
        disease: disease.trim(),
      }),
    });

    if (res.ok) {
      const newPatient = await res.json();
      setPatients([...patients, newPatient]);
      setShowForm(false);
      setName('');
      setAge('');
      setDisease('');
    } else {
      alert('Failed to add patient');
    }
  };

  const startEdit = (patient) => {
    setEditingId(patient.id);
    setEditData({ name: patient.name, age: patient.age, disease: patient.disease });
  };

  const handleUpdate = async (id) => {
    const res = await fetch(`http://localhost:8080/api/patients/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        name: editData.name.trim(),
        age: parseInt(editData.age),
        disease: editData.disease.trim(),
      }),
    });

    if (res.ok) {
      const updated = await res.json();
      setPatients((prev) =>
        prev.map((p) => (p.id === id ? updated : p))
      );
      setEditingId(null);
    } else {
      alert('Failed to update patient');
    }
  };

  const handleDelete = async (id) => {
    const res = await fetch(`http://localhost:8080/api/patients/${id}`, {
      method: 'DELETE',
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    if (res.ok) {
      setPatients((prev) => prev.filter((p) => p.id !== id));
    } else {
      alert('Failed to delete patient');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-2xl font-bold mb-4">Patient Dashboard</h1>

      <button
        className="mb-4 px-4 py-2 bg-blue-500 text-white rounded"
        onClick={() => setShowForm(!showForm)}
      >
        {showForm ? 'Cancel' : 'Add Patient'}
      </button>

      {showForm && (
        <form onSubmit={handleSubmit} className="bg-white p-4 rounded shadow mb-4">
          <input
            type="text"
            placeholder="Name"
            className="block w-full mb-2 p-2 border rounded"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            type="number"
            placeholder="Age"
            className="block w-full mb-2 p-2 border rounded"
            value={age}
            onChange={(e) => setAge(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Disease"
            className="block w-full mb-2 p-2 border rounded"
            value={disease}
            onChange={(e) => setDisease(e.target.value)}
          />
          <button
            type="submit"
            className="px-4 py-2 bg-green-500 text-white rounded"
          >
            Submit
          </button>
        </form>
      )}

      <div className="bg-white shadow p-4 rounded">
        {patients.length === 0 ? (
          <p>No patients found.</p>
        ) : (
          <ul>
            {patients.map((p) => (
              <li key={p.id} className="border-b py-2 flex justify-between items-center">
                {editingId === p.id ? (
                  <div className="flex gap-2 w-full">
                    <input
                      value={editData.name}
                      onChange={(e) =>
                        setEditData({ ...editData, name: e.target.value })
                      }
                      className="border px-2 py-1 rounded w-full"
                      placeholder="Name"
                    />
                    <input
                      value={editData.age}
                      onChange={(e) =>
                        setEditData({ ...editData, age: e.target.value })
                      }
                      className="border px-2 py-1 rounded w-20"
                      placeholder="Age"
                      type="number"
                    />
                    <input
                      value={editData.disease}
                      onChange={(e) =>
                        setEditData({ ...editData, disease: e.target.value })
                      }
                      className="border px-2 py-1 rounded w-full"
                      placeholder="Disease"
                    />
                    <button
                      onClick={() => handleUpdate(p.id)}
                      className="bg-green-500 text-white px-3 py-1 rounded"
                    >
                      Save
                    </button>
                    <button
                      onClick={() => setEditingId(null)}
                      className="bg-gray-400 text-white px-3 py-1 rounded"
                    >
                      Cancel
                    </button>
                  </div>
                ) : (
                  <>
                    <span>
                      <strong>{p.name}</strong> - Age: {p.age}, Disease: {p.disease}
                    </span>
                    <div className="flex gap-2">
                      <button
                        onClick={() => startEdit(p)}
                        className="bg-yellow-500 text-white px-3 py-1 rounded"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDelete(p.id)}
                        className="bg-red-500 text-white px-3 py-1 rounded"
                      >
                        Delete
                      </button>
                    </div>
                  </>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
