import {
  UserPlus,
  CalendarDays,
  FileBarChart2,
  LayoutDashboard,
} from 'lucide-react';
import { Link } from 'react-router-dom';

export default function Sidebar() {
  return (
    <aside className="bg-white shadow-md h-screen w-64 fixed top-0 left-0 z-10 px-6 py-8 space-y-6 border-r hidden md:block">
      <div className="text-2xl font-bold text-blue-600">üè• Hospital</div>
      <nav className="space-y-4 text-gray-700">
        <Link to="/dashboard" className="flex items-center gap-3 hover:text-blue-600 transition">
          <LayoutDashboard size={20} /> Dashboard
        </Link>
        <Link to="/patients" className="flex items-center gap-3 hover:text-blue-600 transition">
          <UserPlus size={20} /> Patients
        </Link>
        <Link to="/appointments" className="flex items-center gap-3 hover:text-blue-600 transition">
          <CalendarDays size={20} /> Appointments
        </Link>
        <Link to="/reports" className="flex items-center gap-3 hover:text-blue-600 transition">
          <FileBarChart2 size={20} /> Reports
        </Link>
      </nav>
    </aside>
  );
}
