import { useState, useEffect } from 'react';

export default function DoctorDashboard() {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editingPatient, setEditingPatient] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [patientHistory, setPatientHistory] = useState([]);
  const [saving, setSaving] = useState(false);
  const token = localStorage.getItem('token');

  // Fetch patients
  const fetchPatients = async () => {
    try {
      setLoading(true);
      console.log('Fetching patients...');
      const response = await fetch('http://localhost:8080/api/patients', {
        headers: { 
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log('Patients fetched:', data);
      setPatients(data || []);
      setError('');
    } catch (err) {
      setError('Failed to load patients');
      console.error('Error fetching patients:', err);
    } finally {
      setLoading(false);
    }
  };

  // Fetch patient history
  const fetchPatientHistory = async (patientId) => {
    try {
      console.log(`Fetching history for patient ${patientId}...`);
      const response = await fetch(`http://localhost:8080/api/patients/${patientId}/history`, {
        headers: { 
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Patient history:', data);
        setPatientHistory(data.medical_history || []);
      } else {
        console.log('No history found or error fetching history');
        setPatientHistory([]);
      }
    } catch (err) {
      console.error('Failed to fetch patient history:', err);
      setPatientHistory([]);
    }
  };

  // Update patient
  const updatePatient = async (e) => {
    e.preventDefault();
    setSaving(true);

    try {
      console.log('Updating patient:', editingPatient);

      const response = await fetch(`http://localhost:8080/api/patients/${editingPatient.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({
          name: editingPatient.name,
          age: editingPatient.age,
          gender: editingPatient.gender,
          phone_number: editingPatient.phone_number,
          diagnosis: editingPatient.diagnosis || '',
          medical_notes: editingPatient.medical_notes || '',
          prescriptions: editingPatient.prescriptions || '',
          last_checkup: editingPatient.last_checkup || '',
          next_appointment: editingPatient.next_appointment || ''
        }),
      });

      if (!response.ok) {
        const errorData = await response.text();
        throw new Error(`Failed to update patient: ${errorData}`);
      }

      const result = await response.json();
      console.log('Update result:', result);

      // Refresh patient list
      await fetchPatients();
      setShowEditModal(false);
      alert('✅ Patient medical record updated successfully!');
    } catch (err) {
      alert(`❌ Error updating patient: ${err.message}`);
      console.error('Update error:', err);
    } finally {
      setSaving(false);
    }
  };

  // Open edit modal
  const handleEdit = async (patient) => {
    console.log('Editing patient:', patient);
    setEditingPatient({
      ...patient,
      medical_notes: patient.medical_notes || '',
      prescriptions: patient.prescriptions || '',
      last_checkup: patient.last_checkup || new Date().toISOString().split('T')[0],
      next_appointment: patient.next_appointment || ''
    });
    await fetchPatientHistory(patient.id);
    setShowEditModal(true);
  };

  // Close edit modal
  const handleCloseModal = () => {
    setShowEditModal(false);
    setEditingPatient(null);
    setPatientHistory([]);
  };

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditingPatient({
      ...editingPatient,
      [name]: value,
    });
  };

  useEffect(() => {
    fetchPatients();
  }, []);

  return (
    <div className="content">
      <div className="content-card">
        <div className="dashboard-header">
          <h1 className="dashboard-title">👨‍⚕️ Doctor Dashboard</h1>
          <p className="dashboard-subtitle">Review and update patient medical records</p>
        </div>

        {/* Stats cards */}
        <div className="stats-grid">
          <div className="stat-card green">
            <div className="stat-info">
              <h3>Total Patients</h3>
              <p>{patients.length}</p>
            </div>
            <div className="stat-icon">👥</div>
          </div>

          <div className="stat-card blue">
            <div className="stat-info">
              <h3>Active Cases</h3>
              <p>{patients.filter(p => p.diagnosis && p.diagnosis !== 'None specified' && p.diagnosis !== '').length}</p>
            </div>
            <div className="stat-icon">📋</div>
          </div>

          <div className="stat-card purple">
            <div className="stat-info">
              <h3>Today's Updates</h3>
              <p>{patients.filter(p => p.updated_at && new Date(p.updated_at).toDateString() === new Date().toDateString()).length}</p>
            </div>
            <div className="stat-icon">🩺</div>
          </div>
        </div>

        {/* Patient Records */}
        <div style={{ marginTop: '40px' }}>
          <h2 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '16px' }}>
            📋 Patient Records
          </h2>
          <p style={{ color: '#6b7280', marginBottom: '24px' }}>
            Click on any patient to view their medical history and update current condition
          </p>

          {loading && (
            <div style={{ textAlign: 'center', padding: '40px' }}>
              <p>🔄 Loading patients...</p>
            </div>
          )}

          {error && (
            <div style={{ textAlign: 'center', padding: '40px' }}>
              <p className="error-message">❌ {error}</p>
              <button 
                onClick={fetchPatients}
                style={{ 
                  marginTop: '10px', 
                  padding: '8px 16px', 
                  backgroundColor: '#3b82f6', 
                  color: 'white', 
                  border: 'none', 
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                🔄 Retry
              </button>
            </div>
          )}

          {!loading && !error && (
            <>
              {patients.length === 0 ? (
                <div style={{ textAlign: 'center', padding: '40px' }}>
                  <p>📭 No patients found.</p>
                </div>
              ) : (
                <div className="patient-grid">
                  {patients.map((patient) => (
                    <div key={patient.id} className="patient-card">
                      <div className="patient-header">
                        <h3 className="patient-name">{patient.name}</h3>
                        <span className="patient-id">ID: {patient.id}</span>
                      </div>

                      <div className="patient-details">
                        <div className="patient-detail">
                          👤 Age: {patient.age} years | Gender: {patient.gender}
                        </div>
                        <div className="patient-detail">
                          📞 Phone: {patient.phone_number}
                        </div>
                        <div className="patient-detail">
                          🏥 Current Diagnosis: {patient.diagnosis || 'None specified'}
                        </div>
                        {patient.prescriptions && (
                          <div className="patient-detail">
                            💊 Current Medications: {patient.prescriptions}
                          </div>
                        )}
                        {patient.last_checkup && (
                          <div className="patient-detail">
                            🗓️ Last Checkup: {patient.last_checkup}
                          </div>
                        )}
                        {patient.updated_at && (
                          <div className="patient-detail">
                            ⏰ Last Updated: {new Date(patient.updated_at).toLocaleDateString()}
                          </div>
                        )}
                      </div>

                      <div className="patient-actions">
                        <button 
                          className="btn btn-edit"
                          onClick={() => handleEdit(patient)}
                        >
                          ✏️ View History & Update
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </>
          )}
        </div>

        {/* Edit Modal with History */}
        {showEditModal && editingPatient && (
          <div className="modal-overlay">
            <div className="modal-content large-modal">
              <div className="modal-header">
                <h2 className="modal-title">🩺 Medical Record - {editingPatient.name}</h2>
                <p className="modal-subtitle">
                  Patient ID: {editingPatient.id} | Age: {editingPatient.age} | Gender: {editingPatient.gender}
                </p>
                <button 
                  className="modal-close"
                  onClick={handleCloseModal}
                  style={{
                    position: 'absolute',
                    top: '16px',
                    right: '16px',
                    background: 'none',
                    border: 'none',
                    fontSize: '24px',
                    cursor: 'pointer'
                  }}
                >
                  ✕
                </button>
              </div>

              <div className="modal-body">
                {/* Medical History Section */}
                <div className="history-section">
                  <h3 className="section-title">📋 Previous Medical History</h3>
                  {patientHistory.length > 0 ? (
                    <div className="history-list">
                      {patientHistory
                        .sort((a, b) => new Date(b.visit_date) - new Date(a.visit_date))
                        .map((history, index) => (
                        <div key={history.id || index} className="history-item">
                          <div className="history-header">
                            <span className="visit-date">
                              🗓️ {new Date(history.visit_date).toLocaleDateString()}
                            </span>
                            <span className="doctor-name">
                              👨‍⚕️ {history.doctor_name}
                            </span>
                          </div>
                          <div className="history-details">
                            {history.diagnosis && (
                              <p><strong>Diagnosis:</strong> {history.diagnosis}</p>
                            )}
                            {history.medical_notes && (
                              <p><strong>Notes:</strong> {history.medical_notes}</p>
                            )}
                            {history.prescriptions && (
                              <p><strong>Prescriptions:</strong> {history.prescriptions}</p>
                            )}
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <p className="no-history">📝 No previous medical history found. This will be the first medical record.</p>
                  )}
                </div>

                {/* Current Update Form */}
                <div className="update-section">
                  <h3 className="section-title">🩺 Current Visit Update</h3>
                  <form onSubmit={updatePatient}>
                    <div className="form-group">
                      <label className="form-label">Current Diagnosis *</label>
                      <textarea
                        name="diagnosis"
                        className="form-input"
                        value={editingPatient.diagnosis || ''}
                        onChange={handleInputChange}
                        placeholder="Enter current diagnosis or condition"
                        rows="3"
                        required
                      />
                    </div>

                    <div className="form-group">
                      <label className="form-label">Medical Notes</label>
                      <textarea
                        name="medical_notes"
                        className="form-input"
                        value={editingPatient.medical_notes || ''}
                        onChange={handleInputChange}
                        placeholder="Enter detailed medical notes, observations, symptoms, etc."
                        rows="4"
                      />
                    </div>

                    <div className="form-group">
                      <label className="form-label">Prescriptions</label>
                      <textarea
                        name="prescriptions"
                        className="form-input"
                        value={editingPatient.prescriptions || ''}
                        onChange={handleInputChange}
                        placeholder="Enter prescribed medications with dosage (e.g., Paracetamol 500mg - 2 times daily)"
                        rows="3"
                      />
                    </div>

                    <div className="form-row">
                      <div className="form-group">
                        <label className="form-label">Checkup Date</label>
                        <input
                          type="date"
                          name="last_checkup"
                          className="form-input"
                          value={editingPatient.last_checkup || ''}
                          onChange={handleInputChange}
                        />
                      </div>

                      <div className="form-group">
                        <label className="form-label">Next Appointment</label>
                        <input
                          type="date"
                          name="next_appointment"
                          className="form-input"
                          value={editingPatient.next_appointment || ''}
                          onChange={handleInputChange}
                        />
                      </div>
                    </div>

                    <div className="form-actions">
                      <button 
                        type="button" 
                        className="btn btn-cancel"
                        onClick={handleCloseModal}
                        disabled={saving}
                      >
                        Cancel
                      </button>
                      <button 
                        type="submit" 
                        className="btn btn-submit"
                        disabled={saving}
                      >
                        {saving ? '💾 Saving...' : '💾 Save Medical Record'}
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Add inline styles for the modal */}
      <style jsx>{`
        .large-modal .modal-content {
          max-width: 1000px;
          max-height: 90vh;
          overflow-y: auto;
          position: relative;
        }

        .modal-body {
          display: flex;
          gap: 30px;
          padding: 20px;
        }

        .history-section {
          flex: 1;
          border-right: 1px solid #e5e7eb;
          padding-right: 20px;
        }

        .update-section {
          flex: 1;
          padding-left: 20px;
        }

        .section-title {
          font-size: 18px;
          font-weight: bold;
          margin-bottom: 16px;
          color: #374151;
        }

        .history-list {
          max-height: 400px;
          overflow-y: auto;
        }

        .history-item {
          background: #f9fafb;
          border: 1px solid #e5e7eb;
          border-radius: 8px;
          padding: 16px;
          margin-bottom: 12px;
        }

        .history-header {
          display: flex;
          justify-content: space-between;
          margin-bottom: 8px;
          font-size: 14px;
          color: #6b7280;
        }

        .history-details p {
          margin: 4px 0;
          font-size: 14px;
        }

        .no-history {
          color: #6b7280;
          font-style: italic;
          text-align: center;
          padding: 20px;
          background: #f9fafb;
          border-radius: 8px;
        }

        .form-row {
          display: flex;
          gap: 16px;
        }

        .form-row .form-group {
          flex: 1;
        }

        .form-group {
          margin-bottom: 16px;
        }

        .form-label {
          display: block;
          margin-bottom: 4px;
          font-weight: 500;
          color: #374151;
        }

        .form-input {
          width: 100%;
          padding: 8px 12px;
          border: 1px solid #d1d5db;
          border-radius: 6px;
          font-size: 14px;
        }

        .form-input:focus {
          outline: none;
          border-color: #3b82f6;
          box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
        }

        .form-actions {
          display: flex;
          gap: 12px;
          justify-content: flex-end;
          margin-top: 24px;
        }

        .btn {
          padding: 10px 20px;
          border: none;
          border-radius: 6px;
          font-weight: 500;
          cursor: pointer;
          transition: all 0.2s;
        }

        .btn:disabled {
          opacity: 0.6;
          cursor: not-allowed;
        }

        .btn-cancel {
          background: #f3f4f6;
          color: #374151;
        }

        .btn-cancel:hover:not(:disabled) {
          background: #e5e7eb;
        }

        .btn-submit {
          background: #3b82f6;
          color: white;
        }

        .btn-submit:hover:not(:disabled) {
          background: #2563eb;
        }
      `}</style>
    </div>
  );
}


