"use client"

import { useEffect, useState } from "react"
import Layout from "../components/Layout"

// Enhanced Toast component
const Toast = ({ message, type, onClose }) => {
  useEffect(() => {
    const timer = setTimeout(() => {
      onClose()
    }, 4000)
    return () => clearTimeout(timer)
  }, [onClose])

  return (
    <div className={`toast ${type === "success" ? "toast-success" : "toast-error"}`}>
      <span className="toast-icon">{type === "success" ? "✅" : "❌"}</span>
      <span className="toast-message">{message}</span>
      <button className="toast-close" onClick={onClose}>
        ×
      </button>
    </div>
  )
}

export default function ReceptionistDashboard() {
  const [patients, setPatients] = useState([])
  const [showForm, setShowForm] = useState(false)
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    gender: "",
    diagnosis: "",
    phone_number: "",
    relationship: "self",
  })
  const [editingId, setEditingId] = useState(null)
  const [toast, setToast] = useState(null)
  const [loading, setLoading] = useState(true)
  const [saving, setSaving] = useState(false)
  const token = localStorage.getItem("token")

  useEffect(() => {
    fetchPatients()
  }, [])

  const fetchPatients = async () => {
    try {
      setLoading(true)
      const res = await fetch("http://localhost:8080/api/patients", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })

      if (res.ok) {
        const data = await res.json()
        setPatients(data || [])
      } else {
        setToast({ message: "Failed to fetch patients", type: "error" })
      }
    } catch (err) {
      console.error("Error fetching patients:", err)
      setToast({ message: "Network error while fetching patients", type: "error" })
    } finally {
      setLoading(false)
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setSaving(true)

    const patientData = {
      name: formData.name.trim(),
      age: Number.parseInt(formData.age),
      gender: formData.gender,
      diagnosis: formData.diagnosis.trim() || "",
      phone_number: formData.phone_number.trim(),
      relationship: formData.relationship,
    }

    try {
      let url = "http://localhost:8080/api/patients"
      let method = "POST"

      if (editingId) {
        url = `http://localhost:8080/api/patients/${editingId}`
        method = "PUT"
      }

      const res = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(patientData),
      })

      if (res.ok) {
        const data = await res.json()

        if (editingId) {
          const updatedPatient = data.patient || data
          setPatients(patients.map((p) => (p.id === editingId ? updatedPatient : p)))
          setToast({ message: "✅ Patient updated successfully!", type: "success" })
        } else {
          setPatients([...patients, data])
          setToast({ message: "✅ Patient added successfully!", type: "success" })
        }

        resetForm()
        await fetchPatients() // Refresh the list
      } else {
        const errorData = await res.json()
        setToast({ message: errorData.error || "Failed to save patient", type: "error" })
      }
    } catch (err) {
      console.error("Error saving patient:", err)
      setToast({ message: "Network error while saving patient", type: "error" })
    } finally {
      setSaving(false)
    }
  }

  const handleDelete = async (id, patientName) => {
    if (!window.confirm(`Are you sure you want to delete ${patientName}?`)) return

    try {
      const res = await fetch(`http://localhost:8080/api/patients/${id}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })

      if (res.ok) {
        setPatients(patients.filter((p) => p.id !== id))
        setToast({ message: `🗑️ ${patientName} deleted successfully!`, type: "success" })
      } else {
        const errorData = await res.json()
        setToast({ message: errorData.error || "Failed to delete patient", type: "error" })
      }
    } catch (err) {
      console.error("Error deleting patient:", err)
      setToast({ message: "Network error while deleting patient", type: "error" })
    }
  }

  const resetForm = () => {
    setFormData({
      name: "",
      age: "",
      gender: "",
      diagnosis: "",
      phone_number: "",
      relationship: "self",
    })
    setShowForm(false)
    setEditingId(null)
  }

  const handleEdit = (patient) => {
    setFormData({
      name: patient.name || "",
      age: String(patient.age) || "",
      gender: patient.gender || "",
      diagnosis: patient.diagnosis || "",
      phone_number: patient.phone_number || "",
      relationship: patient.relationship || "self",
    })
    setEditingId(patient.id)
    setShowForm(true)
  }

  const getRelationshipEmoji = (relationship) => {
    const emojis = {
      self: "👤",
      son: "👦",
      daughter: "👧",
      mother: "👩",
      father: "👨",
      spouse: "💑",
      brother: "👦",
      sister: "👧",
      grandfather: "👴",
      grandmother: "👵",
      other: "👥",
    }
    return emojis[relationship] || "👤"
  }

  const getRelationshipDisplay = (relationship) => {
    const displays = {
      self: "Self",
      son: "Son",
      daughter: "Daughter",
      mother: "Mother",
      father: "Father",
      spouse: "Spouse",
      brother: "Brother",
      sister: "Sister",
      grandfather: "Grandfather",
      grandmother: "Grandmother",
      other: "Other",
    }
    return displays[relationship] || "Unknown"
  }

  if (loading) {
    return (
      <Layout>
        <div className="content">
          <div className="content-card">
            <div style={{ textAlign: "center", padding: "80px 20px" }}>
              <div
                style={{
                  width: "64px",
                  height: "64px",
                  border: "6px solid #e5e7eb",
                  borderTop: "6px solid #667eea",
                  borderRadius: "50%",
                  animation: "spin 1s linear infinite",
                  margin: "0 auto 24px",
                }}
              ></div>
              <h3 style={{ color: "#667eea", fontSize: "20px", marginBottom: "8px" }}>Loading Patient Data</h3>
              <p style={{ color: "#6b7280", fontSize: "16px" }}>Please wait while we fetch the latest information...</p>
            </div>
          </div>
        </div>
      </Layout>
    )
  }

  return (
    <Layout>
      <div className="content">
        <div className="content-card">
          {/* Header */}
          <div className="dashboard-header">
            <h1 className="dashboard-title">👩‍💼 Receptionist Dashboard</h1>
            <p className="dashboard-subtitle">Manage patient registrations and information with ease</p>
          </div>

          {/* Stats Cards */}
          <div className="stats-grid">
            <div className="stat-card green">
              <div className="stat-info">
                <h3>Total Patients</h3>
                <p>{patients.length}</p>
              </div>
              <div className="stat-icon">👥</div>
            </div>

            <div className="stat-card blue">
              <div className="stat-info">
                <h3>Active Cases</h3>
                <p>
                  {patients.filter((p) => p.diagnosis && p.diagnosis !== "None specified" && p.diagnosis !== "").length}
                </p>
              </div>
              <div className="stat-icon">📋</div>
            </div>

            <div className="stat-card purple">
              <div className="stat-info">
                <h3>Family Members</h3>
                <p>{patients.filter((p) => p.relationship !== "self").length}</p>
              </div>
              <div className="stat-icon">👨‍👩‍👧‍👦</div>
            </div>

            <div className="stat-card orange">
              <div className="stat-info">
                <h3>Today's Registrations</h3>
                <p>
                  {
                    patients.filter(
                      (p) => p.created_at && new Date(p.created_at).toDateString() === new Date().toDateString(),
                    ).length
                  }
                </p>
              </div>
              <div className="stat-icon">📅</div>
            </div>
          </div>

          {/* Add Patient Button */}
          <div style={{ textAlign: "center", marginBottom: "40px" }}>
            <button
              className="btn-add"
              onClick={() => {
                resetForm()
                setShowForm(true)
              }}
              style={{
                background: "linear-gradient(135deg, #10b981 0%, #059669 100%)",
                color: "white",
                padding: "16px 32px",
                fontSize: "16px",
                fontWeight: "700",
                border: "none",
                borderRadius: "12px",
                cursor: "pointer",
                boxShadow: "0 10px 30px rgba(16, 185, 129, 0.3)",
                transition: "all 0.3s ease",
                display: "inline-flex",
                alignItems: "center",
                gap: "8px",
              }}
              onMouseOver={(e) => {
                e.target.style.transform = "translateY(-2px)"
                e.target.style.boxShadow = "0 15px 40px rgba(16, 185, 129, 0.4)"
              }}
              onMouseOut={(e) => {
                e.target.style.transform = "translateY(0)"
                e.target.style.boxShadow = "0 10px 30px rgba(16, 185, 129, 0.3)"
              }}
            >
              ➕ Add New Patient
            </button>
          </div>

          {/* Patient Records Section */}
          <div style={{ marginBottom: "32px" }}>
            <h2 style={{ fontSize: "28px", fontWeight: "bold", marginBottom: "8px", color: "#1f2937" }}>
              📋 Patient Records
            </h2>
            <p style={{ color: "#6b7280", marginBottom: "32px", fontSize: "16px" }}>
              Click on any patient to edit their information or manage their records
            </p>

            {patients.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">👥</div>
                <h3 className="empty-title">No Patients Registered Yet</h3>
                <p className="empty-subtitle">Start by adding your first patient to the system.</p>
                <button
                  onClick={() => setShowForm(true)}
                  style={{
                    marginTop: "20px",
                    background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
                    color: "white",
                    padding: "12px 24px",
                    border: "none",
                    borderRadius: "8px",
                    cursor: "pointer",
                    fontWeight: "600",
                  }}
                >
                  Add First Patient
                </button>
              </div>
            ) : (
              <div className="patient-grid">
                {patients.map((patient) => (
                  <div
                    key={patient.id}
                    style={{
                      background: "white",
                      borderRadius: "16px",
                      padding: "24px",
                      boxShadow: "0 4px 20px rgba(0, 0, 0, 0.08)",
                      border: "2px solid #e5e7eb",
                      transition: "all 0.3s ease",
                      cursor: "pointer",
                      position: "relative",
                    }}
                    onMouseOver={(e) => {
                      e.currentTarget.style.transform = "translateY(-4px)"
                      e.currentTarget.style.boxShadow = "0 12px 40px rgba(0, 0, 0, 0.15)"
                      e.currentTarget.style.borderColor = "#667eea"
                    }}
                    onMouseOut={(e) => {
                      e.currentTarget.style.transform = "translateY(0)"
                      e.currentTarget.style.boxShadow = "0 4px 20px rgba(0, 0, 0, 0.08)"
                      e.currentTarget.style.borderColor = "#e5e7eb"
                    }}
                  >
                    {/* Patient Header */}
                    <div
                      style={{
                        display: "flex",
                        justifyContent: "space-between",
                        alignItems: "flex-start",
                        marginBottom: "16px",
                      }}
                    >
                      <div>
                        <h3
                          style={{
                            fontSize: "20px",
                            fontWeight: "700",
                            color: "#1f2937",
                            marginBottom: "4px",
                            display: "flex",
                            alignItems: "center",
                            gap: "8px",
                          }}
                        >
                          {getRelationshipEmoji(patient.relationship)} {patient.name}
                        </h3>
                      </div>
                      <span
                        style={{
                          background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
                          color: "white",
                          padding: "4px 12px",
                          borderRadius: "20px",
                          fontSize: "12px",
                          fontWeight: "600",
                        }}
                      >
                        ID: {patient.id}
                      </span>
                    </div>

                    {/* Patient Details */}
                    <div style={{ marginBottom: "20px" }}>
                      <div
                        style={{
                          display: "flex",
                          alignItems: "center",
                          gap: "8px",
                          marginBottom: "8px",
                          color: "#6b7280",
                          fontWeight: "500",
                        }}
                      >
                        <span>👤</span>
                        <span>
                          Age: {patient.age} years | Gender: {patient.gender || "Not specified"}
                        </span>
                      </div>

                      {patient.phone_number && (
                        <div
                          style={{
                            display: "flex",
                            alignItems: "center",
                            gap: "8px",
                            marginBottom: "8px",
                            color: "#6b7280",
                            fontWeight: "500",
                          }}
                        >
                          <span>📞</span>
                          <span>Phone: {patient.phone_number}</span>
                        </div>
                      )}

                      <div
                        style={{
                          display: "flex",
                          alignItems: "center",
                          gap: "8px",
                          marginBottom: "8px",
                          padding: "8px 12px",
                          background: "#f3f4f6",
                          borderRadius: "8px",
                        }}
                      >
                        <span>👥</span>
                        <span style={{ color: "#4338ca", fontWeight: "600" }}>
                          Relationship: {getRelationshipDisplay(patient.relationship)}
                        </span>
                      </div>

                      <div
                        style={{
                          display: "flex",
                          alignItems: "center",
                          gap: "8px",
                          marginBottom: "8px",
                          color: "#6b7280",
                          fontWeight: "500",
                        }}
                      >
                        <span>🏥</span>
                        <span>Current Diagnosis: {patient.diagnosis || "None specified"}</span>
                      </div>

                      {patient.created_at && (
                        <div
                          style={{
                            display: "flex",
                            alignItems: "center",
                            gap: "8px",
                            color: "#6b7280",
                            fontWeight: "500",
                          }}
                        >
                          <span>⏰</span>
                          <span>Registered: {new Date(patient.created_at).toLocaleDateString()}</span>
                        </div>
                      )}
                    </div>

                    {/* Action Buttons */}
                    <div style={{ display: "flex", gap: "12px" }}>
                      <button
                        onClick={(e) => {
                          e.stopPropagation()
                          handleEdit(patient)
                        }}
                        style={{
                          flex: 1,
                          background: "linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)",
                          color: "white",
                          padding: "12px 16px",
                          border: "none",
                          borderRadius: "8px",
                          cursor: "pointer",
                          fontWeight: "600",
                          fontSize: "14px",
                          transition: "all 0.3s ease",
                        }}
                        onMouseOver={(e) => {
                          e.target.style.transform = "translateY(-1px)"
                          e.target.style.boxShadow = "0 4px 12px rgba(59, 130, 246, 0.3)"
                        }}
                        onMouseOut={(e) => {
                          e.target.style.transform = "translateY(0)"
                          e.target.style.boxShadow = "none"
                        }}
                      >
                        ✏️ Edit Details
                      </button>
                      <button
                        onClick={(e) => {
                          e.stopPropagation()
                          handleDelete(patient.id, patient.name)
                        }}
                        style={{
                          flex: 1,
                          background: "linear-gradient(135deg, #ef4444 0%, #dc2626 100%)",
                          color: "white",
                          padding: "12px 16px",
                          border: "none",
                          borderRadius: "8px",
                          cursor: "pointer",
                          fontWeight: "600",
                          fontSize: "14px",
                          transition: "all 0.3s ease",
                        }}
                        onMouseOver={(e) => {
                          e.target.style.transform = "translateY(-1px)"
                          e.target.style.boxShadow = "0 4px 12px rgba(239, 68, 68, 0.3)"
                        }}
                        onMouseOut={(e) => {
                          e.target.style.transform = "translateY(0)"
                          e.target.style.boxShadow = "none"
                        }}
                      >
                        🗑️ Delete
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Add/Edit Patient Modal */}
          {showForm && (
            <div
              style={{
                position: "fixed",
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                background: "rgba(0, 0, 0, 0.6)",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                zIndex: 1000,
                backdropFilter: "blur(4px)",
              }}
              onClick={(e) => {
                if (e.target === e.currentTarget) resetForm()
              }}
            >
              <div
                style={{
                  background: "white",
                  borderRadius: "20px",
                  padding: "32px",
                  width: "90%",
                  maxWidth: "500px",
                  maxHeight: "90vh",
                  overflowY: "auto",
                  boxShadow: "0 20px 60px rgba(0, 0, 0, 0.3)",
                  border: "1px solid rgba(255, 255, 255, 0.2)",
                }}
              >
                <div style={{ textAlign: "center", marginBottom: "24px" }}>
                  <h2 style={{ fontSize: "28px", fontWeight: "700", color: "#1f2937", marginBottom: "8px" }}>
                    {editingId ? "✏️ Edit Patient" : "➕ Add New Patient"}
                  </h2>
                  <p style={{ color: "#6b7280", fontWeight: "500", fontSize: "16px" }}>
                    {editingId ? "Update patient information" : "Enter patient details below"}
                  </p>
                </div>

                <form onSubmit={handleSubmit}>
                  <div style={{ marginBottom: "20px" }}>
                    <label
                      style={{
                        display: "block",
                        marginBottom: "8px",
                        fontWeight: "600",
                        color: "#374151",
                        fontSize: "14px",
                      }}
                    >
                      👤 Full Name *
                    </label>
                    <input
                      type="text"
                      required
                      value={formData.name}
                      onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                      placeholder="Enter patient's full name"
                      style={{
                        width: "100%",
                        padding: "12px 16px",
                        border: "2px solid #e5e7eb",
                        borderRadius: "8px",
                        fontSize: "16px",
                        transition: "all 0.3s ease",
                        boxSizing: "border-box",
                      }}
                      onFocus={(e) => {
                        e.target.style.borderColor = "#667eea"
                        e.target.style.boxShadow = "0 0 0 3px rgba(102, 126, 234, 0.1)"
                      }}
                      onBlur={(e) => {
                        e.target.style.borderColor = "#e5e7eb"
                        e.target.style.boxShadow = "none"
                      }}
                    />
                  </div>

                  <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "16px", marginBottom: "20px" }}>
                    <div>
                      <label
                        style={{
                          display: "block",
                          marginBottom: "8px",
                          fontWeight: "600",
                          color: "#374151",
                          fontSize: "14px",
                        }}
                      >
                        🎂 Age *
                      </label>
                      <input
                        type="number"
                        required
                        min="0"
                        max="150"
                        value={formData.age}
                        onChange={(e) => setFormData({ ...formData, age: e.target.value })}
                        placeholder="Age"
                        style={{
                          width: "100%",
                          padding: "12px 16px",
                          border: "2px solid #e5e7eb",
                          borderRadius: "8px",
                          fontSize: "16px",
                          transition: "all 0.3s ease",
                          boxSizing: "border-box",
                        }}
                        onFocus={(e) => {
                          e.target.style.borderColor = "#667eea"
                          e.target.style.boxShadow = "0 0 0 3px rgba(102, 126, 234, 0.1)"
                        }}
                        onBlur={(e) => {
                          e.target.style.borderColor = "#e5e7eb"
                          e.target.style.boxShadow = "none"
                        }}
                      />
                    </div>

                    <div>
                      <label
                        style={{
                          display: "block",
                          marginBottom: "8px",
                          fontWeight: "600",
                          color: "#374151",
                          fontSize: "14px",
                        }}
                      >
                        ⚧️ Gender
                      </label>
                      <select
                        value={formData.gender}
                        onChange={(e) => setFormData({ ...formData, gender: e.target.value })}
                        style={{
                          width: "100%",
                          padding: "12px 16px",
                          border: "2px solid #e5e7eb",
                          borderRadius: "8px",
                          fontSize: "16px",
                          transition: "all 0.3s ease",
                          boxSizing: "border-box",
                          cursor: "pointer",
                        }}
                        onFocus={(e) => {
                          e.target.style.borderColor = "#667eea"
                          e.target.style.boxShadow = "0 0 0 3px rgba(102, 126, 234, 0.1)"
                        }}
                        onBlur={(e) => {
                          e.target.style.borderColor = "#e5e7eb"
                          e.target.style.boxShadow = "none"
                        }}
                      >
                        <option value="">Select Gender</option>
                        <option value="Male">Male</option>
                        <option value="Female">Female</option>
                        <option value="Other">Other</option>
                      </select>
                    </div>
                  </div>

                  <div style={{ marginBottom: "20px" }}>
                    <label
                      style={{
                        display: "block",
                        marginBottom: "8px",
                        fontWeight: "600",
                        color: "#374151",
                        fontSize: "14px",
                      }}
                    >
                      📞 Phone Number
                    </label>
                    <input
                      type="tel"
                      value={formData.phone_number}
                      onChange={(e) => setFormData({ ...formData, phone_number: e.target.value })}
                      placeholder="Enter phone number"
                      style={{
                        width: "100%",
                        padding: "12px 16px",
                        border: "2px solid #e5e7eb",
                        borderRadius: "8px",
                        fontSize: "16px",
                        transition: "all 0.3s ease",
                        boxSizing: "border-box",
                      }}
                      onFocus={(e) => {
                        e.target.style.borderColor = "#667eea"
                        e.target.style.boxShadow = "0 0 0 3px rgba(102, 126, 234, 0.1)"
                      }}
                      onBlur={(e) => {
                        e.target.style.borderColor = "#e5e7eb"
                        e.target.style.boxShadow = "none"
                      }}
                    />
                  </div>

                  <div style={{ marginBottom: "20px" }}>
                    <label
                      style={{
                        display: "block",
                        marginBottom: "8px",
                        fontWeight: "600",
                        color: "#374151",
                        fontSize: "14px",
                      }}
                    >
                      👥 Relationship to Phone Number Holder *
                    </label>
                    <select
                      required
                      value={formData.relationship}
                      onChange={(e) => setFormData({ ...formData, relationship: e.target.value })}
                      style={{
                        width: "100%",
                        padding: "12px 16px",
                        border: "2px solid #e5e7eb",
                        borderRadius: "8px",
                        fontSize: "16px",
                        transition: "all 0.3s ease",
                        boxSizing: "border-box",
                        cursor: "pointer",
                      }}
                      onFocus={(e) => {
                        e.target.style.borderColor = "#667eea"
                        e.target.style.boxShadow = "0 0 0 3px rgba(102, 126, 234, 0.1)"
                      }}
                      onBlur={(e) => {
                        e.target.style.borderColor = "#e5e7eb"
                        e.target.style.boxShadow = "none"
                      }}
                    >
                      <option value="self">Self (I am the patient)</option>
                      <option value="son">Son</option>
                      <option value="daughter">Daughter</option>
                      <option value="mother">Mother</option>
                      <option value="father">Father</option>
                      <option value="spouse">Spouse/Partner</option>
                      <option value="brother">Brother</option>
                      <option value="sister">Sister</option>
                      <option value="grandfather">Grandfather</option>
                      <option value="grandmother">Grandmother</option>
                      <option value="other">Other Family Member</option>
                    </select>
                  </div>

                  <div style={{ marginBottom: "24px" }}>
                    <label
                      style={{
                        display: "block",
                        marginBottom: "8px",
                        fontWeight: "600",
                        color: "#374151",
                        fontSize: "14px",
                      }}
                    >
                      🏥 Initial Diagnosis/Condition
                    </label>
                    <textarea
                      value={formData.diagnosis}
                      onChange={(e) => setFormData({ ...formData, diagnosis: e.target.value })}
                      placeholder="Enter initial diagnosis or condition (optional)"
                      rows={3}
                      style={{
                        width: "100%",
                        padding: "12px 16px",
                        border: "2px solid #e5e7eb",
                        borderRadius: "8px",
                        fontSize: "16px",
                        transition: "all 0.3s ease",
                        boxSizing: "border-box",
                        resize: "vertical",
                        fontFamily: "inherit",
                      }}
                      onFocus={(e) => {
                        e.target.style.borderColor = "#667eea"
                        e.target.style.boxShadow = "0 0 0 3px rgba(102, 126, 234, 0.1)"
                      }}
                      onBlur={(e) => {
                        e.target.style.borderColor = "#e5e7eb"
                        e.target.style.boxShadow = "none"
                      }}
                    />
                  </div>

                  <div style={{ display: "flex", gap: "12px" }}>
                    <button
                      type="button"
                      onClick={resetForm}
                      disabled={saving}
                      style={{
                        flex: 1,
                        background: "#f3f4f6",
                        color: "#6b7280",
                        border: "2px solid #e5e7eb",
                        padding: "12px 16px",
                        borderRadius: "8px",
                        cursor: saving ? "not-allowed" : "pointer",
                        fontWeight: "600",
                        fontSize: "16px",
                        transition: "all 0.3s ease",
                      }}
                      onMouseOver={(e) => {
                        if (!saving) {
                          e.target.style.background = "#e5e7eb"
                        }
                      }}
                      onMouseOut={(e) => {
                        if (!saving) {
                          e.target.style.background = "#f3f4f6"
                        }
                      }}
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      disabled={saving}
                      style={{
                        flex: 1,
                        background: saving ? "#93c5fd" : "linear-gradient(135deg, #10b981 0%, #059669 100%)",
                        color: "white",
                        border: "none",
                        padding: "12px 16px",
                        borderRadius: "8px",
                        cursor: saving ? "not-allowed" : "pointer",
                        fontWeight: "600",
                        fontSize: "16px",
                        transition: "all 0.3s ease",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        gap: "8px",
                      }}
                      onMouseOver={(e) => {
                        if (!saving) {
                          e.target.style.transform = "translateY(-1px)"
                          e.target.style.boxShadow = "0 4px 12px rgba(16, 185, 129, 0.3)"
                        }
                      }}
                      onMouseOut={(e) => {
                        if (!saving) {
                          e.target.style.transform = "translateY(0)"
                          e.target.style.boxShadow = "none"
                        }
                      }}
                    >
                      {saving ? (
                        <>
                          <div
                            style={{
                              width: "16px",
                              height: "16px",
                              border: "2px solid white",
                              borderTop: "2px solid transparent",
                              borderRadius: "50%",
                              animation: "spin 1s linear infinite",
                            }}
                          ></div>
                          Saving...
                        </>
                      ) : (
                        <>{editingId ? "✅ Update Patient" : "➕ Add Patient"}</>
                      )}
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}

          {toast && <Toast message={toast.message} type={toast.type} onClose={() => setToast(null)} />}
        </div>
      </div>
    </Layout>
  )
}
