"use client"

import { useEffect, useState } from "react"
import Layout from "../components/Layout"

// Simple Toast component since it's missing
const Toast = ({ message, type, onClose }) => {
  useEffect(() => {
    const timer = setTimeout(() => {
      onClose()
    }, 3000)
    return () => clearTimeout(timer)
  }, [onClose])

  return (
    <div className={`toast ${type === "success" ? "toast-success" : "toast-error"}`}>
      <span className="toast-icon">{type === "success" ? "âœ…" : "âŒ"}</span>
      <span className="toast-message">{message}</span>
      <button className="toast-close" onClick={onClose}>
        âœ•
      </button>
    </div>
  )
}

export default function ReceptionistDashboard() {
  const [patients, setPatients] = useState([])
  const [showForm, setShowForm] = useState(false)
  const [name, setName] = useState("")
  const [age, setAge] = useState("")
  const [gender, setGender] = useState("")
  const [diagnosis, setDiagnosis] = useState("")
  const [phoneNumber, setPhoneNumber] = useState("")
  const [relationship, setRelationship] = useState("self")
  const [editingId, setEditingId] = useState(null)
  const [toast, setToast] = useState(null)
  const [loading, setLoading] = useState(true)
  const token = localStorage.getItem("token")

  useEffect(() => {
    fetchPatients()
  }, [])

  const fetchPatients = async () => {
    try {
      const res = await fetch("http://localhost:8080/api/patients", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })

      if (res.ok) {
        const data = await res.json()
        setPatients(data || [])
      } else {
        setToast({ message: "Failed to fetch patients", type: "error" })
      }
    } catch (err) {
      console.error("Error fetching patients:", err)
      setToast({ message: "Network error while fetching patients", type: "error" })
    } finally {
      setLoading(false)
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    const patientData = {
      name,
      age: Number.parseInt(age),
      gender,
      diagnosis,
      phone_number: phoneNumber,
      relationship,
    }

    try {
      let url = "http://localhost:8080/api/patients"
      let method = "POST"

      if (editingId) {
        url = `http://localhost:8080/api/patients/${editingId}`
        method = "PUT"
      }

      const res = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(patientData),
      })

      if (res.ok) {
        const data = await res.json()

        if (editingId) {
          const updatedPatient = data.patient || data
          setPatients(patients.map((p) => (p.id === editingId ? updatedPatient : p)))
          setToast({ message: "Patient updated successfully!", type: "success" })
        } else {
          setPatients([...patients, data])
          setToast({ message: "Patient added successfully!", type: "success" })
        }

        resetForm()
      } else {
        const errorData = await res.json()
        setToast({ message: errorData.error || "Failed to save patient", type: "error" })
      }
    } catch (err) {
      console.error("Error saving patient:", err)
      setToast({ message: "Network error while saving patient", type: "error" })
    }
  }

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this patient?")) return

    try {
      const res = await fetch(`http://localhost:8080/api/patients/${id}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })

      if (res.ok) {
        setPatients(patients.filter((p) => p.id !== id))
        setToast({ message: "Patient deleted successfully!", type: "success" })
      } else {
        const errorData = await res.json()
        setToast({ message: errorData.error || "Failed to delete patient", type: "error" })
      }
    } catch (err) {
      console.error("Error deleting patient:", err)
      setToast({ message: "Network error while deleting patient", type: "error" })
    }
  }

  const resetForm = () => {
    setName("")
    setAge("")
    setGender("")
    setDiagnosis("")
    setPhoneNumber("")
    setRelationship("self")
    setShowForm(false)
    setEditingId(null)
  }

  const handleEdit = (patient) => {
    setName(patient.name)
    setAge(String(patient.age))
    setGender(patient.gender || "")
    setDiagnosis(patient.diagnosis || "")
    setPhoneNumber(patient.phone_number || "")
    setRelationship(patient.relationship || "self")
    setEditingId(patient.id)
    setShowForm(true)
  }

  if (loading) {
    return (
      <Layout>
        <div className="content">
          <div className="content-card">
            <div style={{ textAlign: "center", padding: "60px 20px" }}>
              <div
                style={{
                  width: "48px",
                  height: "48px",
                  border: "4px solid #e5e7eb",
                  borderTop: "4px solid #667eea",
                  borderRadius: "50%",
                  animation: "spin 1s linear infinite",
                  margin: "0 auto 16px",
                }}
              ></div>
              <p style={{ color: "#6b7280", fontSize: "16px" }}>Loading patients...</p>
            </div>
          </div>
        </div>
      </Layout>
    )
  }

  return (
    <Layout>
      <div className="content">
        <div className="content-card">
          <div className="dashboard-header">
            <h1 className="dashboard-title">ğŸ‘©â€ğŸ’¼ Receptionist Dashboard</h1>
            <p className="dashboard-subtitle">Manage patient registrations and information</p>
          </div>

          <div className="stats-grid">
            <div className="stat-card blue">
              <div className="stat-info">
                <h3>Total Patients</h3>
                <p>{patients.length}</p>
              </div>
              <div className="stat-icon">ğŸ‘¥</div>
            </div>

            <div className="stat-card green">
              <div className="stat-info">
                <h3>Active Cases</h3>
                <p>{patients.filter((p) => p.diagnosis && p.diagnosis !== "N/A").length}</p>
              </div>
              <div className="stat-icon">ğŸ“‹</div>
            </div>

            <div className="stat-card purple">
              <div className="stat-info">
                <h3>New Today</h3>
                <p>
                  {
                    patients.filter(
                      (p) => p.created_at && new Date(p.created_at).toDateString() === new Date().toDateString(),
                    ).length
                  }
                </p>
              </div>
              <div className="stat-icon">ğŸ“…</div>
            </div>

            <div className="stat-card orange">
              <div className="stat-info">
                <h3>Family Members</h3>
                <p>{patients.filter((p) => p.relationship !== "self").length}</p>
              </div>
              <div className="stat-icon">ğŸ‘¨â€ğŸ‘©â€ğŸ‘§â€ğŸ‘¦</div>
            </div>
          </div>

          <div style={{ textAlign: "center", marginBottom: "32px" }}>
            <button className="btn-add" onClick={() => setShowForm(!showForm)}>
              â• Add New Patient
            </button>
          </div>

          {showForm && (
            <div className="modal-overlay">
              <div className="modal-content">
                <div className="modal-header">
                  <h2 className="modal-title">{editingId ? "Edit Patient" : "Add New Patient"}</h2>
                  <p className="modal-subtitle">
                    {editingId ? "Update patient information" : "Enter patient details below"}
                  </p>
                </div>

                <form onSubmit={handleSubmit}>
                  <div className="form-group">
                    <label className="form-label">ğŸ‘¤ Full Name</label>
                    <input
                      type="text"
                      className="form-input"
                      placeholder="Enter full name"
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">ğŸ‚ Age</label>
                    <input
                      type="number"
                      className="form-input"
                      placeholder="Enter age"
                      value={age}
                      onChange={(e) => setAge(e.target.value)}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">âš§ï¸ Gender</label>
                    <select
                      className="form-input"
                      value={gender}
                      onChange={(e) => setGender(e.target.value)}
                      style={{ paddingLeft: "16px" }}
                    >
                      <option value="">Select gender</option>
                      <option value="Male">Male</option>
                      <option value="Female">Female</option>
                      <option value="Other">Other</option>
                    </select>
                  </div>

                  <div className="form-group">
                    <label className="form-label">ğŸ‘¥ Relationship to Phone Number Holder</label>
                    <select
                      className="form-input"
                      value={relationship}
                      onChange={(e) => setRelationship(e.target.value)}
                      required
                    >
                      <option value="self">Self (I am the patient)</option>
                      <option value="son">Son</option>
                      <option value="daughter">Daughter</option>
                      <option value="mother">Mother</option>
                      <option value="father">Father</option>
                      <option value="spouse">Spouse/Partner</option>
                      <option value="brother">Brother</option>
                      <option value="sister">Sister</option>
                      <option value="grandfather">Grandfather</option>
                      <option value="grandmother">Grandmother</option>
                      <option value="other">Other Family Member</option>
                    </select>
                  </div>

                  <div className="form-group">
                    <label className="form-label">ğŸ¥ Diagnosis/Condition</label>
                    <input
                      type="text"
                      className="form-input"
                      placeholder="Enter diagnosis or condition (optional)"
                      value={diagnosis}
                      onChange={(e) => setDiagnosis(e.target.value)}
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">ğŸ“ Phone Number</label>
                    <input
                      type="text"
                      className="form-input"
                      placeholder="Enter phone number"
                      value={phoneNumber}
                      onChange={(e) => setPhoneNumber(e.target.value)}
                    />
                  </div>

                  <div className="form-actions">
                    <button type="button" className="btn btn-cancel" onClick={resetForm}>
                      Cancel
                    </button>
                    <button type="submit" className="btn btn-submit">
                      {editingId ? "Update Patient" : "Add Patient"}
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}

          <div style={{ marginBottom: "32px" }}>
            <h2 style={{ fontSize: "24px", fontWeight: "bold", marginBottom: "16px", color: "#1f2937" }}>
              ğŸ“‹ Patients List
            </h2>
            <p style={{ color: "#6b7280", marginBottom: "24px" }}>
              Manage all registered patients and their information
            </p>

            {patients.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">ğŸ‘¥</div>
                <h3 className="empty-title">No Patients Yet</h3>
                <p className="empty-subtitle">Start by adding your first patient to the system.</p>
              </div>
            ) : (
              <div className="patient-grid">
                {patients.map((patient) => (
                  <div key={patient.id} className="patient-card">
                    <div className="patient-header">
                      <div>
                        <h3 className="patient-name">
                          {patient.relationship === "self" ? "ğŸ‘¤" : "ğŸ‘¥"} {patient.name}
                        </h3>
                        <span className="patient-id">ID: {patient.id}</span>
                      </div>
                      {patient.relationship !== "self" && (
                        <span
                          style={{
                            background: "#e0e7ff",
                            color: "#4338ca",
                            padding: "4px 12px",
                            borderRadius: "20px",
                            fontSize: "12px",
                            fontWeight: "600",
                          }}
                        >
                          {patient.relationship}
                        </span>
                      )}
                    </div>

                    <div className="patient-details">
                      <div className="patient-detail">
                        <span>ğŸ‚</span>
                        <span>Age: {patient.age} years</span>
                      </div>
                      {patient.gender && (
                        <div className="patient-detail">
                          <span>âš§ï¸</span>
                          <span>Gender: {patient.gender}</span>
                        </div>
                      )}
                      <div className="patient-detail">
                        <span>ğŸ¥</span>
                        <span>Diagnosis: {patient.diagnosis || "None specified"}</span>
                      </div>
                      {patient.phone_number && (
                        <div className="patient-detail">
                          <span>ğŸ“</span>
                          <span>Phone: {patient.phone_number}</span>
                        </div>
                      )}
                      {patient.relationship && (
                        <div className="patient-detail">
                          <span>ğŸ‘¥</span>
                          <span>Relationship: {patient.relationship}</span>
                        </div>
                      )}
                      {patient.created_at && (
                        <div className="patient-detail">
                          <span>ğŸ“…</span>
                          <span>Registered: {new Date(patient.created_at).toLocaleDateString()}</span>
                        </div>
                      )}
                    </div>

                    <div className="patient-actions">
                      <button onClick={() => handleEdit(patient)} className="btn btn-edit">
                        âœï¸ Edit
                      </button>
                      <button onClick={() => handleDelete(patient.id)} className="btn btn-delete">
                        ğŸ—‘ï¸ Delete
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {toast && <Toast message={toast.message} type={toast.type} onClose={() => setToast(null)} />}
        </div>
      </div>
    </Layout>
  )
}
