import { useEffect, useState } from 'react';

export default function PatientDashboard() {
  const [patients, setPatients] = useState([]);
  const token = localStorage.getItem('token');

  useEffect(() => {
    console.log("üü° Fetching patients with token:", token);

    fetch('http://localhost:8080/api/patients', {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json"
      },
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error("Failed to fetch patients");
        }
        return res.json();
      })
      .then((data) => {
        console.log("‚úÖ Patients fetched:", data);
        setPatients(data);
      })
      .catch((err) => {
        console.error("‚ùå Fetch error:", err);
        alert("Failed to connect to backend. Please check if Go server is running.");
      });
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-2xl font-bold mb-4">Patient Dashboard</h1>
      <div className="bg-white shadow p-4 rounded">
        {patients.length === 0 ? (
          <p>No patients found.</p>
        ) : (
          <ul>
            {patients.map((p) => (
              <li key={p.id} className="border-b py-2">
                <strong>{p.name}</strong> - Age: {p.age}, Disease: {p.disease}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
