"use client"

import { useState } from "react"
import { X } from "lucide-react" // Add this import for close icon

const DoctorDashboard = () => {
  const [patients, setPatients] = useState([])
  const [selectedPatient, setSelectedPatient] = useState(null)
  const [familyHistory, setFamilyHistory] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")
  const [showModal, setShowModal] = useState(false)

  // ... existing useEffect and functions ...

  const handleEditPatient = async (patient) => {
    setSelectedPatient({
      ...patient,
      medical_notes: patient.medical_notes || "",
      prescriptions: patient.prescriptions || "",
      last_checkup: patient.last_checkup || "",
      next_appointment: patient.next_appointment || "",
    })

    // Fetch family history
    try {
      const response = await fetch(`/api/patients/family-history/${patient.phone_number}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      })

      if (response.ok) {
        const history = await response.json()
        setFamilyHistory(history)
      }
    } catch (error) {
      console.error("Error fetching family history:", error)
    }

    setShowModal(true) // Show modal instead of direct state
  }

  const closeModal = () => {
    setShowModal(false)
    setSelectedPatient(null)
    setFamilyHistory([])
  }

  const handleSavePatient = async () => {
    try {
      const response = await fetch(`/api/patients/${selectedPatient.id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify(selectedPatient),
      })

      if (response.ok) {
        const updatedPatient = await response.json()
        setPatients(patients.map((p) => (p.id === updatedPatient.id ? updatedPatient : p)))
        closeModal() // Use closeModal function
        alert("Patient updated successfully!")
      } else {
        alert("Failed to update patient")
      }
    } catch (error) {
      console.error("Error updating patient:", error)
      alert("Error updating patient")
    }
  }

  // ... existing JSX until modal part ...

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <h1 className="text-2xl font-bold text-gray-900">👨‍⚕️ Doctor Dashboard</h1>
            <button
              onClick={() => {
                localStorage.removeItem("token")
                window.location.href = "/"
              }}
              className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg transition-colors"
            >
              Logout
            </button>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {loading && <div className="text-center py-8">Loading patients...</div>}
        {error && <div className="text-red-600 text-center py-8">{error}</div>}

        {!loading && !error && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {patients.map((patient) => (
              <div key={patient.id} className="bg-white rounded-lg shadow-md p-6 border border-gray-200">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg font-semibold text-gray-900">👤 {patient.name}</h3>
                  <span className="text-sm text-blue-600 font-medium">ID: {patient.id}</span>
                </div>

                <div className="space-y-2 text-sm text-gray-600">
                  <p>
                    👤 Age: {patient.age} years | Gender: {patient.gender}
                  </p>
                  <p>📞 Phone: {patient.phone_number}</p>
                  <p>👥 Relationship: {patient.relationship || "Unknown"}</p>
                  <p>🏥 Current Diagnosis: {patient.diagnosis || "None specified"}</p>
                  <p>
                    🕒 Last Updated: {patient.updated_at ? new Date(patient.updated_at).toLocaleDateString() : "N/A"}
                  </p>
                </div>

                <button
                  onClick={() => handleEditPatient(patient)}
                  className="mt-4 w-full bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-lg transition-colors"
                >
                  📝 Add First Medical Record
                </button>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Enhanced Modal with larger size and close button */}
      {showModal && selectedPatient && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-6xl max-h-[90vh] overflow-y-auto">
            {/* Modal Header with Close Button */}
            <div className="flex items-center justify-between p-6 border-b border-gray-200 sticky top-0 bg-white">
              <h2 className="text-2xl font-bold text-gray-900">📋 Medical Record - 👤 {selectedPatient.name}</h2>
              <button
                onClick={closeModal}
                className="p-2 hover:bg-gray-100 rounded-full transition-colors"
                aria-label="Close modal"
              >
                <X size={24} className="text-gray-500 hover:text-gray-700" />
              </button>
            </div>

            <div className="p-6">
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                {/* Left Column - Patient Info & Family History */}
                <div className="space-y-6">
                  {/* Patient Information */}
                  <div className="bg-blue-50 p-4 rounded-lg">
                    <h3 className="text-lg font-semibold text-blue-800 mb-3">👤 Patient Information</h3>
                    <div className="space-y-2 text-sm">
                      <p>
                        <strong>📞 Phone:</strong> {selectedPatient.phone_number}
                      </p>
                      <p>
                        <strong>👥 Relationship:</strong> {selectedPatient.relationship || "Unknown"}
                      </p>
                      <p>
                        <strong>👤 Age:</strong> {selectedPatient.age} | <strong>Gender:</strong>{" "}
                        {selectedPatient.gender}
                      </p>
                    </div>
                  </div>

                  {/* Family History */}
                  {familyHistory.length > 0 && (
                    <div className="bg-green-50 p-4 rounded-lg">
                      <h3 className="text-lg font-semibold text-green-800 mb-3">
                        👨‍👩‍👧‍👦 Family History ({familyHistory.length} total visits)
                      </h3>
                      <div className="space-y-3 max-h-64 overflow-y-auto">
                        {familyHistory.map((record, index) => (
                          <div key={index} className="bg-white p-3 rounded border border-green-200">
                            <div className="flex items-center justify-between mb-2">
                              <span className="font-medium text-green-700">
                                👤 {record.patient_name} ({record.relationship || "Unknown"}, Age: {record.age})
                              </span>
                              <span className="text-xs text-gray-500">
                                {record.visit_date ? new Date(record.visit_date).toLocaleDateString() : "Today"}
                              </span>
                            </div>
                            <p className="text-sm">
                              <strong>🏥 Diagnosis:</strong> {record.diagnosis}
                            </p>
                            <p className="text-sm">
                              <strong>📝 Notes:</strong> {record.medical_notes}
                            </p>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </div>

                {/* Right Column - Current Visit Form */}
                <div className="space-y-6">
                  <div className="bg-yellow-50 p-4 rounded-lg">
                    <h3 className="text-lg font-semibold text-yellow-800 mb-4">
                      🏥 Current Visit - 👤 {selectedPatient.name}
                    </h3>

                    <div className="space-y-4">
                      {/* Current Diagnosis */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">🏥 Current Diagnosis *</label>
                        <input
                          type="text"
                          value={selectedPatient.diagnosis || ""}
                          onChange={(e) => setSelectedPatient({ ...selectedPatient, diagnosis: e.target.value })}
                          className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                          placeholder="What is the current medical condition or complaint?"
                        />
                      </div>

                      {/* Medical Notes */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">📝 Medical Notes</label>
                        <textarea
                          value={selectedPatient.medical_notes || ""}
                          onChange={(e) => setSelectedPatient({ ...selectedPatient, medical_notes: e.target.value })}
                          rows="4"
                          className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                          placeholder="Detailed observations, symptoms, examination findings..."
                        />
                      </div>

                      {/* Prescriptions */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">💊 Prescriptions</label>
                        <textarea
                          value={selectedPatient.prescriptions || ""}
                          onChange={(e) => setSelectedPatient({ ...selectedPatient, prescriptions: e.target.value })}
                          rows="3"
                          className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                          placeholder="Medications with dosage and frequency..."
                        />
                      </div>

                      {/* Dates */}
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-2">🩺 Checkup Date</label>
                          <input
                            type="date"
                            value={selectedPatient.last_checkup || ""}
                            onChange={(e) => setSelectedPatient({ ...selectedPatient, last_checkup: e.target.value })}
                            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                          />
                        </div>
                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-2">📅 Next Appointment</label>
                          <input
                            type="date"
                            value={selectedPatient.next_appointment || ""}
                            onChange={(e) =>
                              setSelectedPatient({ ...selectedPatient, next_appointment: e.target.value })
                            }
                            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                          />
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex justify-end space-x-4 mt-8 pt-6 border-t border-gray-200">
                <button
                  onClick={closeModal}
                  className="px-6 py-3 bg-gray-500 hover:bg-gray-600 text-white rounded-lg transition-colors"
                >
                  Cancel
                </button>
                <button
                  onClick={handleSavePatient}
                  className="px-6 py-3 bg-blue-500 hover:bg-blue-600 text-white rounded-lg transition-colors"
                >
                  💾 Save Medical Record
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

export default DoctorDashboard
