import { useEffect, useState } from 'react';

export default function ReceptionistDashboard() {
  const [patients, setPatients] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [disease, setDisease] = useState('');
  const [editingId, setEditingId] = useState(null);
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetch('http://localhost:8080/api/patients', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setPatients(data));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = { name, age: parseInt(age), disease };

    const url = editingId
      ? `http://localhost:8080/api/patients/${editingId}`
      : 'http://localhost:8080/api/patients';

    const method = editingId ? 'PUT' : 'POST';

    const res = await fetch(url, {
      method,
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(data),
    });

    if (res.ok) {
      const result = await res.json();
      if (editingId) {
        setPatients(patients.map((p) => (p.id === result.id ? result : p)));
      } else {
        setPatients([...patients, result]);
      }
      resetForm();
    } else {
      alert('‚ùå Failed to save patient');
    }
  };

  const handleDelete = async (id) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this patient?');
    if (!confirmDelete) return;

    const res = await fetch(`http://localhost:8080/api/patients/${id}`, {
      method: 'DELETE',
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    if (res.ok) {
      setPatients(patients.filter((p) => p.id !== id));
    } else {
      alert('‚ùå Failed to delete patient');
    }
  };

  const resetForm = () => {
    setName('');
    setAge('');
    setDisease('');
    setShowForm(false);
    setEditingId(null);
  };

  const handleEdit = (patient) => {
    setName(patient.name);
    setAge(String(patient.age));
    setDisease(patient.disease);
    setEditingId(patient.id);
    setShowForm(true);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-yellow-100 to-white p-8 font-sans">
      <h1 className="text-4xl font-extrabold text-center text-yellow-700 mb-10">
        üè• Receptionist Dashboard
      </h1>

      <div className="flex justify-center mb-6">
        <button
          className="bg-yellow-500 hover:bg-yellow-600 text-white px-6 py-2 rounded-xl shadow-md font-semibold transition-all"
          onClick={() => setShowForm(!showForm)}
        >
          {showForm ? 'Cancel' : '‚ûï Add Patient'}
        </button>
      </div>

      {showForm && (
        <form
          onSubmit={handleSubmit}
          className="max-w-xl mx-auto bg-white border border-gray-200 rounded-xl shadow-lg p-6 mb-10 space-y-4"
        >
          <div className="text-lg font-medium text-gray-700">
            {editingId ? '‚úèÔ∏è Edit Patient Info' : 'üìù Add New Patient'}
          </div>

          <input
            type="text"
            placeholder="Full Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-500"
            required
          />
          <input
            type="number"
            placeholder="Age"
            value={age}
            onChange={(e) => setAge(e.target.value)}
            className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-500"
            required
          />
          <input
            type="text"
            placeholder="Disease"
            value={disease}
            onChange={(e) => setDisease(e.target.value)}
            className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-500"
          />
          <button
            type="submit"
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 rounded-md transition-all shadow-md"
          >
            {editingId ? '‚úÖ Update Patient' : 'üöÄ Submit Patient'}
          </button>
        </form>
      )}

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {patients.map((p) => (
          <div
            key={p.id}
            className="bg-white rounded-xl p-6 border border-gray-200 shadow hover:shadow-xl transition duration-300"
          >
            <h2 className="text-xl font-bold text-blue-700 mb-1">{p.name}</h2>
            <p className="text-sm text-gray-600">Age: {p.age}</p>
            <p className="text-sm text-gray-600 mb-4">Disease: {p.disease || 'N/A'}</p>
            <div className="flex gap-3">
              <button
                onClick={() => handleEdit(p)}
                className="bg-yellow-400 hover:bg-yellow-500 text-white px-4 py-1 rounded"
              >
                ‚úèÔ∏è Edit
              </button>
              <button
                onClick={() => handleDelete(p.id)}
                className="bg-red-500 hover:bg-red-600 text-white px-4 py-1 rounded"
              >
                üóëÔ∏è Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
