import { useState, useEffect } from 'react';

export default function DoctorDashboard() {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editingPatient, setEditingPatient] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const token = localStorage.getItem('token');

  // Fetch patients
  const fetchPatients = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/patients', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setPatients(data);
    } catch (err) {
      setError('Failed to load patients');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Update patient
  const updatePatient = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`http://localhost:8080/api/patients/${editingPatient.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(editingPatient),
      });

      if (!response.ok) {
        throw new Error('Failed to update patient');
      }

      // Refresh patient list
      fetchPatients();
      setShowEditModal(false);
      alert('Patient updated successfully');
    } catch (err) {
      alert('Error updating patient');
      console.error(err);
    }
  };

  // Open edit modal
  const handleEdit = (patient) => {
    setEditingPatient({...patient});
    setShowEditModal(true);
  };

  // Close edit modal
  const handleCloseModal = () => {
    setShowEditModal(false);
    setEditingPatient(null);
  };

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditingPatient({
      ...editingPatient,
      [name]: value,
    });
  };

  useEffect(() => {
    fetchPatients();
  }, []);

  return (
    <div className="content">
      <div className="content-card">
        <div className="dashboard-header">
          <h1 className="dashboard-title">Doctor Dashboard</h1>
          <p className="dashboard-subtitle">Review and update patient medical records</p>
        </div>

        {/* Stats cards */}
        <div className="stats-grid">
          <div className="stat-card green">
            <div className="stat-info">
              <h3>Total Patients</h3>
              <p>{patients.length}</p>
            </div>
            <div className="stat-icon">👥</div>
          </div>

          <div className="stat-card blue">
            <div className="stat-info">
              <h3>Active Cases</h3>
              <p>{patients.filter(p => p.diagnosis && p.diagnosis !== 'None specified').length}</p>
            </div>
            <div className="stat-icon">📋</div>
          </div>

          <div className="stat-card purple">
            <div className="stat-info">
              <h3>Consultations</h3>
              <p>24</p>
            </div>
            <div className="stat-icon">🩺</div>
          </div>
        </div>

        {/* Patient Records */}
        <div style={{ marginTop: '40px' }}>
          <h2 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '16px' }}>
            📋 Patient Records
          </h2>
          <p style={{ color: '#6b7280', marginBottom: '24px' }}>
            Click on any patient to update their medical information
          </p>

          {loading && <p>Loading patients...</p>}
          {error && <p className="error-message">{error}</p>}

          {!loading && !error && (
            <>
              {patients.length === 0 ? (
                <p>No patients found.</p>
              ) : (
                <div className="patient-grid">
                  {patients.map((patient) => (
                    <div key={patient.id} className="patient-card">
                      <div className="patient-header">
                        <h3 className="patient-name">{patient.name}</h3>
                        <span className="patient-id">ID: {patient.id}</span>
                      </div>

                      <div className="patient-details">
                        <div className="patient-detail">
                          👤 Age: {patient.age} years
                        </div>
                        <div className="patient-detail">
                          🏥 Diagnosis: {patient.diagnosis || 'None specified'}
                        </div>
                        {patient.prescriptions && (
                          <div className="patient-detail">
                            💊 Medications: {patient.prescriptions}
                          </div>
                        )}
                        {patient.last_checkup && (
                          <div className="patient-detail">
                            🗓️ Last Checkup: {patient.last_checkup}
                          </div>
                        )}
                      </div>

                      <div className="patient-actions">
                        <button 
                          className="btn btn-edit"
                          onClick={() => handleEdit(patient)}
                        >
                          ✏️ Update Medical Record
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </>
          )}
        </div>

        {/* Edit Modal */}
        {showEditModal && editingPatient && (
          <div className="modal-overlay">
            <div className="modal-content">
              <div className="modal-header">
                <h2 className="modal-title">Update Medical Record</h2>
                <p className="modal-subtitle">Patient: {editingPatient.name}</p>
              </div>

              <form onSubmit={updatePatient}>
                <div className="form-group">
                  <label className="form-label">Diagnosis</label>
                  <textarea
                    name="diagnosis"
                    className="form-input"
                    value={editingPatient.diagnosis || ''}
                    onChange={handleInputChange}
                    placeholder="Enter diagnosis"
                    rows="3"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Medical Notes</label>
                  <textarea
                    name="medical_notes"
                    className="form-input"
                    value={editingPatient.medical_notes || ''}
                    onChange={handleInputChange}
                    placeholder="Enter medical notes"
                    rows="4"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Prescriptions</label>
                  <textarea
                    name="prescriptions"
                    className="form-input"
                    value={editingPatient.prescriptions || ''}
                    onChange={handleInputChange}
                    placeholder="Enter prescribed medications"
                    rows="3"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Last Checkup Date</label>
                  <input
                    type="date"
                    name="last_checkup"
                    className="form-input"
                    value={editingPatient.last_checkup || ''}
                    onChange={handleInputChange}
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Next Appointment</label>
                  <input
                    type="date"
                    name="next_appointment"
                    className="form-input"
                    value={editingPatient.next_appointment || ''}
                    onChange={handleInputChange}
                  />
                </div>

                <div className="form-actions">
                  <button 
                    type="button" 
                    className="btn btn-cancel"
                    onClick={handleCloseModal}
                  >
                    Cancel
                  </button>
                  <button 
                    type="submit" 
                    className="btn btn-submit"
                  >
                    Save Medical Record
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}



