"use client"

import { useEffect, useState } from "react"

const DoctorDashboard = () => {
  const [patients, setPatients] = useState([])
  const [selectedPatient, setSelectedPatient] = useState(null)
  const [showUpdateForm, setShowUpdateForm] = useState(false)
  const [updateData, setUpdateData] = useState({
    diagnosis: "",
    medical_notes: "",
    prescriptions: "",
    next_appointment: "",
  })
  const [loading, setLoading] = useState(true)

  const fetchPatients = async () => {
    try {
      const token = localStorage.getItem("token")
      const res = await fetch("http://localhost:8080/api/patients", {
        headers: { Authorization: `Bearer ${token}` },
      })

      if (res.ok) {
        const data = await res.json()
        setPatients(data || [])
      } else {
        console.error("Failed to fetch patients")
      }
    } catch (error) {
      console.error("Error fetching patients:", error)
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchPatients()
  }, [])

  const handleUpdatePatient = async (e) => {
    e.preventDefault()

    if (!selectedPatient) return

    try {
      const token = localStorage.getItem("token")
      const res = await fetch(`http://localhost:8080/api/patients/${selectedPatient.id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          ...selectedPatient,
          ...updateData,
        }),
      })

      if (res.ok) {
        alert("‚úÖ Patient updated successfully")
        await fetchPatients()
        setShowUpdateForm(false)
        setSelectedPatient(null)
        setUpdateData({
          diagnosis: "",
          medical_notes: "",
          prescriptions: "",
          next_appointment: "",
        })
      } else {
        const errorData = await res.json()
        alert(`‚ùå Failed to update patient: ${errorData.error}`)
      }
    } catch (error) {
      alert("‚ùå Network error")
    }
  }

  const openUpdateForm = (patient) => {
    setSelectedPatient(patient)
    setUpdateData({
      diagnosis: patient.diagnosis || "",
      medical_notes: patient.medical_notes || "",
      prescriptions: patient.prescriptions || "",
      next_appointment: patient.next_appointment || "",
    })
    setShowUpdateForm(true)
  }

  const closeUpdateForm = () => {
    setShowUpdateForm(false)
    setSelectedPatient(null)
    setUpdateData({
      diagnosis: "",
      medical_notes: "",
      prescriptions: "",
      next_appointment: "",
    })
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-gradient-to-r from-green-600 to-blue-600 text-white p-6 rounded-lg">
        <h1 className="text-3xl font-bold mb-2">üë®‚Äç‚öïÔ∏è Doctor Dashboard</h1>
        <p className="text-green-100">View and manage patient medical records</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="bg-white p-6 rounded-lg shadow-md border-l-4 border-blue-500">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-600 text-sm">Total Patients</p>
              <p className="text-3xl font-bold text-gray-900">{patients.length}</p>
            </div>
            <div className="text-4xl text-blue-500">üë•</div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-md border-l-4 border-green-500">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-600 text-sm">Active Cases</p>
              <p className="text-3xl font-bold text-gray-900">
                {patients.filter((p) => p.diagnosis && p.diagnosis.trim() !== "").length}
              </p>
            </div>
            <div className="text-4xl text-green-500">üè•</div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-md border-l-4 border-purple-500">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-600 text-sm">With Prescriptions</p>
              <p className="text-3xl font-bold text-gray-900">
                {patients.filter((p) => p.prescriptions && p.prescriptions.trim() !== "").length}
              </p>
            </div>
            <div className="text-4xl text-purple-500">üíä</div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-md border-l-4 border-orange-500">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-600 text-sm">Follow-ups</p>
              <p className="text-3xl font-bold text-gray-900">
                {patients.filter((p) => p.next_appointment && p.next_appointment.trim() !== "").length}
              </p>
            </div>
            <div className="text-4xl text-orange-500">üìÖ</div>
          </div>
        </div>
      </div>

      {/* Patient Records */}
      <div className="bg-white rounded-lg shadow-md">
        <div className="px-6 py-4 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-900">üìã Patient Records</h2>
        </div>

        {loading ? (
          <div className="p-6 text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-green-500 mx-auto"></div>
            <p className="mt-2 text-gray-600">Loading patients...</p>
          </div>
        ) : patients.length === 0 ? (
          <div className="p-6 text-center text-gray-500">
            <div className="text-4xl mb-2">üìã</div>
            <p>No patient records found.</p>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Patient Info
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Contact
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Medical Status
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {patients.map((patient) => (
                  <tr key={patient.id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <div>
                          <div className="text-sm font-medium text-gray-900">{patient.name}</div>
                          <div className="text-sm text-gray-500">
                            ID: {patient.id} ‚Ä¢ Age: {patient.age} ‚Ä¢ {patient.gender || "N/A"}
                          </div>
                          {patient.relationship && (
                            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-purple-100 text-purple-800 mt-1">
                              {patient.relationship}
                            </span>
                          )}
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {patient.phone_number || "N/A"}
                    </td>
                    <td className="px-6 py-4">
                      <div className="text-sm text-gray-900">
                        <div className="font-medium">{patient.diagnosis || "No diagnosis"}</div>
                        {patient.medical_notes && (
                          <div className="text-gray-500 mt-1">
                            Notes: {patient.medical_notes.substring(0, 50)}
                            {patient.medical_notes.length > 50 ? "..." : ""}
                          </div>
                        )}
                        {patient.prescriptions && <div className="text-blue-600 mt-1">üíä Prescribed</div>}
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      <button
                        onClick={() => openUpdateForm(patient)}
                        className="bg-green-600 text-white px-3 py-1 rounded text-sm hover:bg-green-700 transition-colors"
                      >
                        üìù Update
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Update Patient Modal */}
      {showUpdateForm && selectedPatient && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-1/2 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <h3 className="text-lg font-medium text-gray-900 mb-4">
                üìù Update Medical Record - {selectedPatient.name}
              </h3>

              <form onSubmit={handleUpdatePatient} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Diagnosis</label>
                  <input
                    type="text"
                    value={updateData.diagnosis}
                    onChange={(e) => setUpdateData({ ...updateData, diagnosis: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
                    placeholder="Enter diagnosis"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Medical Notes</label>
                  <textarea
                    value={updateData.medical_notes}
                    onChange={(e) => setUpdateData({ ...updateData, medical_notes: e.target.value })}
                    rows={3}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
                    placeholder="Enter medical notes"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Prescriptions</label>
                  <textarea
                    value={updateData.prescriptions}
                    onChange={(e) => setUpdateData({ ...updateData, prescriptions: e.target.value })}
                    rows={3}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
                    placeholder="Enter prescriptions"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Next Appointment</label>
                  <input
                    type="text"
                    value={updateData.next_appointment}
                    onChange={(e) => setUpdateData({ ...updateData, next_appointment: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
                    placeholder="e.g., Next week, 2024-01-15, etc."
                  />
                </div>

                <div className="flex space-x-3 pt-4">
                  <button
                    type="submit"
                    className="bg-green-600 text-white px-6 py-2 rounded-lg font-medium hover:bg-green-700 transition-colors flex items-center gap-2"
                  >
                    <span>üíæ</span>
                    Update Record
                  </button>
                  <button
                    type="button"
                    onClick={closeUpdateForm}
                    className="bg-gray-500 text-white px-6 py-2 rounded-lg font-medium hover:bg-gray-600 transition-colors"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

export default DoctorDashboard
