import { useEffect, useState } from 'react';

export default function DoctorDashboard() {
  const [patients, setPatients] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [disease, setDisease] = useState('');
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetch('http://localhost:8080/api/patients', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setPatients(data));
  }, []);

  const handleEdit = (patient) => {
    setEditingId(patient.id);
    setName(patient.name);
    setAge(String(patient.age));
    setDisease(patient.disease);
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const updatedData = { name, age: parseInt(age), disease };

    const res = await fetch(`http://localhost:8080/api/patients/${editingId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(updatedData),
    });

    if (res.ok) {
      const updatedPatient = await res.json();
      setPatients(patients.map((p) => (p.id === updatedPatient.id ? updatedPatient : p)));
      resetForm();
    } else {
      alert('Failed to update patient');
    }
  };

  const resetForm = () => {
    setEditingId(null);
    setName('');
    setAge('');
    setDisease('');
  };

  return (
    <div className="p-6 bg-gradient-to-r from-blue-100 to-teal-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-4">Doctor Dashboard</h1>

      {editingId && (
        <form onSubmit={handleUpdate} className="bg-white shadow p-4 mb-4 rounded">
          <h2 className="text-xl mb-2">Edit Patient</h2>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="block w-full mb-2 p-2 border rounded"
            required
          />
          <input
            type="number"
            value={age}
            onChange={(e) => setAge(e.target.value)}
            className="block w-full mb-2 p-2 border rounded"
            required
          />
          <input
            type="text"
            value={disease}
            onChange={(e) => setDisease(e.target.value)}
            className="block w-full mb-2 p-2 border rounded"
          />
          <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded">
            Update
          </button>
          <button
            type="button"
            className="ml-2 bg-gray-400 text-white px-4 py-2 rounded"
            onClick={resetForm}
          >
            Cancel
          </button>
        </form>
      )}

      <ul className="bg-white p-4 rounded shadow">
        {patients.map((p) => (
          <li key={p.id} className="mb-2 flex justify-between items-center border-b py-2">
            <div>
              <strong>{p.name}</strong> - Age: {p.age}, Disease: {p.disease || 'N/A'}
            </div>
            <button
              className="bg-blue-500 text-white px-3 py-1 rounded"
              onClick={() => handleEdit(p)}
            >
              Edit
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
