import { useState, useEffect } from 'react';

export default function DoctorDashboard() {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const token = localStorage.getItem('token');

  // Function to fetch patients from API
  const fetchPatients = async () => {
    try {
      console.log('Fetching patients for doctor...'); // Debug log

      const response = await fetch('http://localhost:8080/api/patients', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      console.log('Response status:', response.status); // Debug log

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log('Fetched patients:', data); // Debug log

      setPatients(data || []);
      setError('');
    } catch (err) {
      console.error('Error fetching patients:', err);
      setError('Failed to load patients');
    } finally {
      setLoading(false);
    }
  };

  // Fetch patients when component mounts
  useEffect(() => {
    if (token) {
      fetchPatients();
    } else {
      setError('No authentication token found');
      setLoading(false);
    }
  }, [token]);

  return (
    <div className="content">
      <div className="content-card">
        <div className="dashboard-header">
          <h1 className="dashboard-title">Doctor Dashboard</h1>
          <p className="dashboard-subtitle">Review and update patient medical records</p>
        </div>

        {/* Statistics Cards */}
        <div className="stats-grid">
          <div className="stat-card green">
            <div className="stat-info">
              <h3>Total Patients</h3>
              <p>{patients.length}</p>
            </div>
            <div className="stat-icon">👥</div>
          </div>

          <div className="stat-card blue">
            <div className="stat-info">
              <h3>Active Cases</h3>
              <p>{patients.filter(p => p.diagnosis && p.diagnosis !== 'None specified').length}</p>
            </div>
            <div className="stat-icon">📋</div>
          </div>

          <div className="stat-card purple">
            <div className="stat-info">
              <h3>Consultations</h3>
              <p>24</p>
            </div>
            <div className="stat-icon">🩺</div>
          </div>

          <div className="stat-card orange">
            <div className="stat-info">
              <h3>Reports</h3>
              <p>12</p>
            </div>
            <div className="stat-icon">📊</div>
          </div>
        </div>

        {/* Patient Records Section */}
        <div style={{ marginTop: '40px' }}>
          <h2 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '16px' }}>
            📋 Patient Records
          </h2>
          <p style={{ color: '#6b7280', marginBottom: '24px' }}>
            Click on any patient to update their medical information
          </p>

          {/* Loading State */}
          {loading && (
            <div style={{ textAlign: 'center', padding: '40px' }}>
              <p>Loading patients...</p>
            </div>
          )}

          {/* Error State */}
          {error && (
            <div style={{ 
              background: '#fee2e2', 
              color: '#dc2626', 
              padding: '16px', 
              borderRadius: '8px',
              marginBottom: '20px'
            }}>
              <p>❌ {error}</p>
            </div>
          )}

          {/* Patients List */}
          {!loading && !error && (
            <>
              {patients.length === 0 ? (
                <div className="empty-state">
                  <div className="empty-icon">🏥</div>
                  <h3 className="empty-title">No patients found</h3>
                  <p className="empty-subtitle">
                    No patients have been registered yet. Ask the receptionist to add some patients.
                  </p>
                </div>
              ) : (
                <div className="patient-grid">
                  {patients.map((patient) => (
                    <div key={patient.id} className="patient-card">
                      <div className="patient-header">
                        <h3 className="patient-name">{patient.name}</h3>
                        <span className="patient-id">ID: {patient.id}</span>
                      </div>

                      <div className="patient-details">
                        <div className="patient-detail">
                          👤 Age: {patient.age} years
                        </div>
                        <div className="patient-detail">
                          🏥 Diagnosis: {patient.diagnosis || 'None specified'}
                        </div>
                        {patient.phone_number && (
                          <div className="patient-detail">
                            📞 Phone: {patient.phone_number}
                          </div>
                        )}
                      </div>

                      <div className="patient-actions">
                        <button 
                          className="btn btn-edit"
                          onClick={() => {
                            // Add edit functionality here
                            alert(`Edit patient: ${patient.name}`);
                          }}
                        >
                          ✏️ Edit
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
}






