import { useEffect, useState } from 'react';

export default function PatientDashboard() {
  const [patients, setPatients] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [disease, setDisease] = useState('');
  const [editingId, setEditingId] = useState(null);
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetch('http://localhost:8080/api/patients', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setPatients(data));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = { name, age: parseInt(age), disease };

    const url = editingId
      ? `http://localhost:8080/api/patients/${editingId}`
      : 'http://localhost:8080/api/patients';

    const method = editingId ? 'PUT' : 'POST';

    const res = await fetch(url, {
      method,
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(data),
    });

    if (res.ok) {
      const result = await res.json();
      if (editingId) {
        setPatients(patients.map((p) => (p.id === result.id ? result : p)));
      } else {
        setPatients([...patients, result]);
      }
      resetForm();
    } else {
      alert("Failed to save patient");
    }
  };

  const resetForm = () => {
    setName('');
    setAge('');
    setDisease('');
    setShowForm(false);
    setEditingId(null);
  };

  const handleEdit = (patient) => {
    setName(patient.name);
    setAge(String(patient.age));
    setDisease(patient.disease);
    setEditingId(patient.id);
    setShowForm(true);
  };

  return (
    <div className="p-6 min-h-screen bg-gradient-to-r from-indigo-100 to-purple-100">
      <h1 className="text-3xl font-bold mb-4">Patient Dashboard</h1>

      <button
        className="mb-4 bg-blue-500 text-white px-4 py-2 rounded"
        onClick={() => setShowForm(!showForm)}
      >
        {showForm ? 'Cancel' : 'Add Patient'}
      </button>

      {showForm && (
        <form onSubmit={handleSubmit} className="bg-white shadow p-4 mb-4 rounded">
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="block w-full mb-2 p-2 border rounded"
            required
          />
          <input
            type="number"
            placeholder="Age"
            value={age}
            onChange={(e) => setAge(e.target.value)}
            className="block w-full mb-2 p-2 border rounded"
            required
          />
          <input
            type="text"
            placeholder="Disease"
            value={disease}
            onChange={(e) => setDisease(e.target.value)}
            className="block w-full mb-2 p-2 border rounded"
          />
          <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">
            {editingId ? 'Update' : 'Submit'}
          </button>
        </form>
      )}

      <ul className="bg-white p-4 rounded shadow">
        {patients.map((p) => (
          <li key={p.id} className="mb-2 flex justify-between items-center border-b py-2">
            <div>
              <strong>{p.name}</strong> - Age: {p.age}, Disease: {p.disease || 'N/A'}
            </div>
            <button
              className="bg-yellow-400 text-white px-3 py-1 rounded"
              onClick={() => handleEdit(p)}
            >
              Edit
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
