import { useEffect, useState } from 'react';
import Toast from '../components/Toast';

export default function DoctorDashboard() {
  const [patients, setPatients] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [gender, setGender] = useState('');
  const [diagnosis, setDiagnosis] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [toast, setToast] = useState(null);
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      const res = await fetch('http://localhost:8080/api/patients', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (res.ok) {
        const data = await res.json();
        setPatients(data);
      } else {
        setToast({ message: 'Failed to fetch patients', type: 'error' });
      }
    } catch (err) {
      console.error('Error fetching patients:', err);
      setToast({ message: 'Network error while fetching patients', type: 'error' });
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (patient) => {
    setEditingId(patient.id);
    setName(patient.name);
    setAge(String(patient.age));
    setGender(patient.gender || '');
    setDiagnosis(patient.diagnosis || '');
    setPhoneNumber(patient.phone_number || '');
  };

  const handleUpdate = async (e) => {
    e.preventDefault();

    const patientData = {
      name,
      age: parseInt(age),
      gender,
      diagnosis,
      phone_number: phoneNumber
    };

    try {
      const res = await fetch(`http://localhost:8080/api/patients/${editingId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(patientData)
      });

      if (res.ok) {
        const updatedPatient = await res.json();
        setPatients(patients.map(p => p.id === editingId ? updatedPatient : p));
        setToast({ message: 'Patient updated successfully!', type: 'success' });
        resetForm();
      } else {
        const errorData = await res.json();
        setToast({ message: errorData.error || 'Failed to update patient', type: 'error' });
      }
    } catch (err) {
      console.error('Error updating patient:', err);
      setToast({ message: 'Network error while updating patient', type: 'error' });
    }
  };

  const resetForm = () => {
    setEditingId(null);
    setName('');
    setAge('');
    setGender('');
    setDiagnosis('');
    setPhoneNumber('');
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div>
      <div className="dashboard-header">
        <h1 className="dashboard-title">Doctor Dashboard</h1>
        <p className="dashboard-subtitle">Review and update patient medical records</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card green">
          <div className="stat-info">
            <h3>Total Patients</h3>
            <p>{patients.length}</p>
          </div>
          <div className="stat-icon">ğŸ‘¥</div>
        </div>

        <div className="stat-card blue">
          <div className="stat-info">
            <h3>Active Cases</h3>
            <p>{patients.filter(p => p.diagnosis).length}</p>
          </div>
          <div className="stat-icon">ğŸ“‹</div>
        </div>

        <div className="stat-card purple">
          <div className="stat-info">
            <h3>Consultations</h3>
            <p>24</p>
          </div>
          <div className="stat-icon">ğŸ©º</div>
        </div>

        <div className="stat-card orange">
          <div className="stat-info">
            <h3>Reports</h3>
            <p>12</p>
          </div>
          <div className="stat-icon">ğŸ“„</div>
        </div>
      </div>

      {editingId && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2 className="modal-title">Update Patient Record</h2>
              <p className="modal-subtitle">Modify patient information and medical details</p>
            </div>

            <form onSubmit={handleUpdate}>
              <div className="form-group">
                <label className="form-label">Patient Name</label>
                <input
                  type="text"
                  className="form-input"
                  placeholder="Enter patient name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                  style={{ paddingLeft: '16px' }}
                />
              </div>

              <div className="form-group">
                <label className="form-label">Age</label>
                <input
                  type="number"
                  className="form-input"
                  placeholder="Enter age"
                  value={age}
                  onChange={(e) => setAge(e.target.value)}
                  required
                  style={{ paddingLeft: '16px' }}
                />
              </div>

              <div className="form-group">
                <label className="form-label">Gender</label>
                <select
                  className="form-select"
                  value={gender}
                  onChange={(e) => setGender(e.target.value)}
                  style={{ paddingLeft: '16px' }}
                >
                  <option value="">Select gender</option>
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                  <option value="Other">Other</option>
                </select>
              </div>

              <div className="form-group">
                <label className="form-label">Medical Diagnosis</label>
                <textarea
                  className="form-input"
                  placeholder="Enter diagnosis, symptoms, or medical notes..."
                  value={diagnosis}
                  onChange={(e) => setDiagnosis(e.target.value)}
                  rows="4"
                  style={{ paddingLeft: '16px', minHeight: '100px', resize: 'vertical' }}
                />
              </div>

              <div className="form-group">
                <label className="form-label">Phone Number</label>
                <input
                  type="text"
                  className="form-input"
                  placeholder="Enter phone number"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  style={{ paddingLeft: '16px' }}
                />
              </div>

              <div className="form-actions">
                <button type="button" className="btn btn-cancel" onClick={resetForm}>
                  Cancel
                </button>
