                    <span style={{ background: '#dcfce7', color: '#166534', padding: '4px 12px', borderRadius: '20px', fontSize: '12px', fontWeight: '600' }}>
                      ID: {patient.id}
                    </span>
                  </div>
                  <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '16px', color: '#6b7280' }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                      <span>ğŸ‘¤</span>
                      <span>Age: {patient.age} years</span>
                    </div>
                    {patient.gender && (
                      <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                        <span>âš§ï¸</span>
                        <span>Gender: {patient.gender}</span>
                      </div>
                    )}
                    <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                      <span>ğŸ¥</span>
                      <span>Diagnosis: {patient.diagnosis || 'No diagnosis recorded'}</span>
                    </div>
                  </div>
                </div>
                <button
                  style={{
                    background: '#f0fdf4',
                    color: '#166534',
                    border: '1px solid #bbf7d0',
                    padding: '8px 16px',
                    borderRadius: '8px',
                    fontSize: '14px',
                    fontWeight: '600',
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '4px'
                  }}
                >
                  âœï¸ Edit
                </button>
              </div>
            </div>
          ))}
        </div>

        {patients.length === 0 && (
          <div className="empty-state">
            <div className="empty-icon">ğŸ©º</div>
            <h3 className="empty-title">No Patient Records</h3>
            <p className="empty-subtitle">Patient records will appear here once they are registered.</p>
          </div>
        )}
      </div>

      {toast && (
        <Toast 
          message={toast.message} 
          type={toast.type} 
          onClose={() => setToast(null)} 
        />
      )}
    </div>
  );
}
