"use client"

import { useState } from "react"

const LoginPage = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    role: "receptionist",
  })
  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)

  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)
    setError("")

    try {
      console.log("🔄 Attempting login with:", { email: formData.email, role: formData.role })

      // 🔧 CORRECT ENDPOINT: /auth/login (not /api/login)
      const response = await fetch("http://localhost:8080/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: formData.email,
          password: formData.password,
        }),
      })

      console.log("📡 Response status:", response.status)

      if (!response.ok) {
        const errorText = await response.text()
        throw new Error(`Login failed: ${errorText}`)
      }

      const data = await response.json()
      console.log("✅ Login successful:", data)

      // ✅ Validate role matches
      if (data.user && data.user.role !== formData.role) {
        setError(
          `❌ Access denied. You are registered as a ${data.user.role}, but trying to login as ${formData.role}.`,
        )
        setLoading(false)
        return
      }

      // Store token and redirect
      if (data.token) {
        localStorage.setItem("token", data.token)
        localStorage.setItem("user", JSON.stringify(data.user))

        console.log("🚀 Redirecting to dashboard...")

        if (data.user.role === "doctor") {
          window.location.href = "/doctor"
        } else if (data.user.role === "receptionist") {
          window.location.href = "/receptionist"
        }
      } else {
        throw new Error("No token received from server")
      }
    } catch (error) {
      console.error("🚨 Login error:", error)
      setError(`🚨 ${error.message}`)
    }

    setLoading(false)
  }

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        {/* Header */}
        <div style={styles.header}>
          <div style={styles.icon}>
            <span>🏥</span>
          </div>
          <h2 style={styles.title}>Hospital Portal</h2>
          <p style={styles.subtitle}>Sign in to your account</p>
        </div>

        {/* Error Message */}
        {error && (
          <div style={styles.errorMessage}>
            <p>{error}</p>
          </div>
        )}

        {/* Form */}
        <form onSubmit={handleSubmit} style={styles.form}>
          {/* Email Field */}
          <div style={styles.formGroup}>
            <label htmlFor="email" style={styles.label}>
              📧 Email Address
            </label>
            <input
              id="email"
              type="email"
              required
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              style={styles.input}
              placeholder="Enter your email"
            />
          </div>

          {/* Password Field */}
          <div style={styles.formGroup}>
            <label htmlFor="password" style={styles.label}>
              🔒 Password
            </label>
            <input
              id="password"
              type="password"
              required
              value={formData.password}
              onChange={(e) => setFormData({ ...formData, password: e.target.value })}
              style={styles.input}
              placeholder="Enter your password"
            />
          </div>

          {/* Role Selection */}
          <div style={styles.formGroup}>
            <label htmlFor="role" style={styles.label}>
              👤 Login as
            </label>
            <select
              id="role"
              value={formData.role}
              onChange={(e) => setFormData({ ...formData, role: e.target.value })}
              style={styles.input}
            >
              <option value="receptionist">👩‍💼 Receptionist</option>
              <option value="doctor">👨‍⚕️ Doctor</option>
            </select>
          </div>

          {/* Submit Button */}
          <button type="submit" disabled={loading} style={loading ? styles.buttonDisabled : styles.button}>
            {loading ? "🔄 Signing in..." : "🚀 Sign In"}
          </button>
        </form>

        {/* Footer */}
        <div style={styles.footer}>
          <p>
            Server: <span style={styles.serverUrl}>http://localhost:8080</span>
          </p>
          <p style={styles.debugInfo}>
            Endpoint: <span style={styles.serverUrl}>/auth/login</span>
          </p>
        </div>
      </div>
    </div>
  )
}

// Inline styles
const styles = {
  container: {
    minHeight: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    background: "linear-gradient(135deg, #e0f2fe 0%, #e1e5fe 50%, #f3e5f5 100%)",
    padding: "20px",
    fontFamily: "Arial, sans-serif",
  },
  card: {
    maxWidth: "400px",
    width: "100%",
    background: "white",
    borderRadius: "16px",
    boxShadow: "0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)",
    padding: "32px",
    border: "1px solid #f3f4f6",
  },
  header: {
    textAlign: "center",
    marginBottom: "32px",
  },
  icon: {
    margin: "0 auto 16px auto",
    width: "64px",
    height: "64px",
    background: "#dbeafe",
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "32px",
  },
  title: {
    fontSize: "30px",
    fontWeight: "bold",
    color: "#111827",
    margin: "0 0 8px 0",
  },
  subtitle: {
    color: "#6b7280",
    margin: "0",
  },
  errorMessage: {
    marginBottom: "24px",
    padding: "16px",
    background: "#fef2f2",
    border: "1px solid #fecaca",
    borderRadius: "8px",
    color: "#b91c1c",
    fontSize: "14px",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "24px",
  },
  formGroup: {
    display: "flex",
    flexDirection: "column",
  },
  label: {
    display: "block",
    fontSize: "14px",
    fontWeight: "500",
    color: "#374151",
    marginBottom: "8px",
  },
  input: {
    width: "100%",
    padding: "12px 16px",
    border: "1px solid #d1d5db",
    borderRadius: "8px",
    fontSize: "16px",
    transition: "all 0.2s",
    boxSizing: "border-box",
  },
  button: {
    width: "100%",
    background: "#2563eb",
    color: "white",
    fontWeight: "500",
    padding: "12px 16px",
    border: "none",
    borderRadius: "8px",
    fontSize: "16px",
    cursor: "pointer",
    transition: "all 0.2s",
  },
  buttonDisabled: {
    width: "100%",
    background: "#93c5fd",
    color: "white",
    fontWeight: "500",
    padding: "12px 16px",
    border: "none",
    borderRadius: "8px",
    fontSize: "16px",
    cursor: "not-allowed",
  },
  footer: {
    marginTop: "24px",
    textAlign: "center",
  },
  serverUrl: {
    fontFamily: "monospace",
    color: "#2563eb",
    fontWeight: "bold",
  },
  debugInfo: {
    fontSize: "12px",
    color: "#6b7280",
    margin: "4px 0 0 0",
  },
}

export default LoginPage
