import { NavLink, useNavigate } from 'react-router-dom';
import { Users, Stethoscope, LogOut, Hospital, Home } from 'lucide-react';

export default function Sidebar() {
  const navigate = useNavigate();
  const role = localStorage.getItem('role');

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/login');
  };

  const navItems = [
    {
      to: role === 'doctor' ? '/doctor' : '/receptionist',
      icon: Home,
      label: 'Dashboard',
      color: 'text-blue-400',
    },
    ...(role === 'receptionist' ? [{
      to: '/receptionist',
      icon: Users,
      label: 'Receptionist',
      color: 'text-green-400',
    }] : []),
    ...(role === 'doctor' ? [{
      to: '/doctor',
      icon: Stethoscope,
      label: 'Doctor',
      color: 'text-purple-400',
    }] : []),
  ];

  return (
    <div className="w-72 h-screen bg-gradient-to-b from-slate-900 via-purple-900 to-slate-900 text-white fixed left-0 top-0 shadow-2xl z-40">
      <div className="p-6 border-b border-white/10">
        <div className="flex items-center gap-3">
          <div className="p-2 bg-gradient-to-r from-blue-500 to-purple-600 rounded-xl">
            <Hospital className="h-8 w-8 text-white" />
          </div>
          <div>
            <h2 className="text-xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
              MediCare Portal
            </h2>
            <p className="text-sm text-gray-400">Healthcare Management</p>
          </div>
        </div>
      </div>

      <nav className="flex flex-col p-6 gap-3 h-full">
        <div className="flex-1">
          {navItems.map((item) => {
            const Icon = item.icon;
            return (
              <NavLink
                key={item.to}
                to={item.to}
                className={({ isActive }) => `
                  flex items-center gap-4 px-4 py-3 rounded-xl transition-all duration-300 group mb-2
                  ${isActive
                    ? 'bg-gradient-to-r from-blue-600/20 to-purple-600/20 border border-blue-500/30 shadow-lg'
                    : 'hover:bg-white/5 hover:translate-x-1'
                  }
                `}
              >
                {({ isActive }) => (
                  <>
                    <Icon className={`h-5 w-5 ${isActive ? item.color : 'text-gray-400 group-hover:text-white'}`} />
                    <span className={`font-medium transition-colors ${isActive ? 'text-white' : 'text-gray-300 group-hover:text-white'}`}>
                      {item.label}
                    </span>
                  </>
                )}
              </NavLink>
            );
          })}
        </div>

        <div className="pt-6 border-t border-white/10">
          <button 
            onClick={handleLogout}
            className="flex items-center gap-4 px-4 py-3 rounded-xl w-full text-left bg-gradient-to-r from-red-600/20 to-pink-600/20 border border-red-500/30 hover:from-red-600/30 hover:to-pink-600/30 transition-all duration-300 group"
          >
            <LogOut className="h-5 w-5 text-red-400 group-hover:text-red-300" />
            <span className="font-medium text-red-300 group-hover:text-red-200">Logout</span>
          </button>
        </div>
      </nav>
    </div>
  );
}
