import { useEffect, useState } from 'react';

export default function ReceptionistDashboard() {
  const [patients, setPatients] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [disease, setDisease] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [toast, setToast] = useState(null);

  // Mock data for testing
  useEffect(() => {
    const mockPatients = [
      { id: 1, name: 'John Doe', age: 35, disease: 'Hypertension' },
      { id: 2, name: 'Jane Smith', age: 28, disease: 'Diabetes' },
      { id: 3, name: 'Bob Johnson', age: 45, disease: 'Asthma' },
    ];
    setPatients(mockPatients);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newPatient = {
      id: editingId || Date.now(),
      name,
      age: parseInt(age),
      disease
    };

    if (editingId) {
      setPatients(patients.map(p => p.id === editingId ? newPatient : p));
      setToast({ message: 'Patient updated successfully!', type: 'success' });
    } else {
      setPatients([...patients, newPatient]);
      setToast({ message: 'Patient added successfully!', type: 'success' });
    }

    resetForm();
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this patient?')) {
      setPatients(patients.filter(p => p.id !== id));
      setToast({ message: 'Patient deleted successfully!', type: 'success' });
    }
  };

  const resetForm = () => {
    setName('');
    setAge('');
    setDisease('');
    setShowForm(false);
    setEditingId(null);
  };

  const handleEdit = (patient) => {
    setName(patient.name);
    setAge(String(patient.age));
    setDisease(patient.disease);
    setEditingId(patient.id);
    setShowForm(true);
  };

  return (
    <div>
      <div className="dashboard-header">
        <h1 className="dashboard-title">Receptionist Dashboard</h1>
        <p className="dashboard-subtitle">Manage patient registrations and information</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card blue">
          <div className="stat-info">
            <h3>Total Patients</h3>
            <p>{patients.length}</p>
          </div>
          <div className="stat-icon">👥</div>
        </div>

        <div className="stat-card green">
          <div className="stat-info">
            <h3>Active Cases</h3>
            <p>{patients.filter(p => p.disease).length}</p>
          </div>
          <div className="stat-icon">📋</div>
        </div>

        <div className="stat-card purple">
          <div className="stat-info">
            <h3>Today</h3>
            <p>{new Date().getDate()}</p>
          </div>
          <div className="stat-icon">📅</div>
        </div>
      </div>

      <div style={{ textAlign: 'center', marginBottom: '32px' }}>
        <button
          className="btn-add"
          onClick={() => setShowForm(!showForm)}
        >
          ➕ Add New Patient
        </button>
      </div>

      {showForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2 className="modal-title">
                {editingId ? 'Edit Patient' : 'Add New Patient'}
              </h2>
              <p className="modal-subtitle">
                {editingId ? 'Update patient information' : 'Enter patient details below'}
              </p>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label className="form-label">Full Name</label>
                <input
                  type="text"
                  className="form-input"
                  placeholder="Enter full name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                  style={{ paddingLeft: '16px' }}
                />
              </div>

              <div className="form-group">
                <label className="form-label">Age</label>
                <input
                  type="number"
                  className="form-input"
                  placeholder="Enter age"
                  value={age}
                  onChange={(e) => setAge(e.target.value)}
                  required
                  style={{ paddingLeft: '16px' }}
                />
              </div>

              <div className="form-group">
                <label className="form-label">Disease/Condition</label>
                <input
                  type="text"
                  className="form-input"
                  placeholder="Enter disease or condition (optional)"
                  value={disease}
                  onChange={(e) => setDisease(e.target.value)}
                  style={{ paddingLeft: '16px' }}
                />
              </div>

              <div className="form-actions">
                <button type="button" className="btn btn-cancel" onClick={resetForm}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-submit">
                  {editingId ? 'Update Patient' : 'Add Patient'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="patient-grid">
        {patients.map((patient) => (
          <div key={patient.id} className="patient-card">
            <div className="patient-header">
              <div>
                <h3 className="patient-name">{patient.name}</h3>
                <span className="patient-id">ID: {patient.id}</span>
              </div>
            </div>

            <div className="patient-details">
              <div className="patient-detail">
                <span>👤</span>
                <span>Age: {patient.age} years</span>
              </div>
              <div className="patient-detail">
                <span>🏥</span>
                <span>Condition: {patient.disease || 'None specified'}</span>
              </div>
            </div>

            <div className="patient-actions">
              <button
                onClick={() => handleEdit(patient)}
                className="btn btn-edit"
              >
                ✏️ Edit
              </button>
              <button
                onClick={() => handleDelete(patient.id)}
                className="btn btn-delete"
              >
                🗑️ Delete
              </button>
            </div>
          </div>
        ))}
      </div>

      {patients.length === 0 && (
        <div className="empty-state">
          <div className="empty-icon">👥</div>
          <h3 className="empty-title">No Patients Yet</h3>
          <p className="empty-subtitle">Start by adding your first patient to the system.</p>
        </div>
      )}

      {toast && (
        <div className={`toast ${toast.type === 'success' ? 'toast-success' : 'toast-error'}`}>
          <span className="toast-icon">{toast.type === 'success' ? '✅' : '❌'}</span>
          <span className="toast-message">{toast.message}</span>
          <button className="toast-close" onClick={() => setToast(null)}>
            ×
          </button>
        </div>
      )}
    </div>
  );
}
