import { Bell, User } from 'lucide-react';
import LogoutButton from './LogoutButton';

export default function Navbar({ user = "Healthcare Professional" }) {
  return (
    <header className="bg-gradient-to-r from-blue-600 via-purple-600 to-indigo-700 text-white px-6 py-4 shadow-xl border-b border-white/10 ml-72">
      <div className="flex justify-between items-center max-w-7xl mx-auto">
        <div className="flex items-center gap-4">
          <div className="flex items-center gap-3">
            <div className="p-2 bg-white/10 rounded-xl backdrop-blur-sm">
              <div className="w-8 h-8 bg-gradient-to-r from-blue-400 to-purple-400 rounded-lg flex items-center justify-center">
                <span className="text-white font-bold text-lg">H</span>
              </div>
            </div>
            <div>
              <h1 className="text-2xl font-bold tracking-wide">Hospital Portal</h1>
              <p className="text-blue-100 text-sm">Advanced Healthcare Management</p>
            </div>
          </div>
        </div>

        <div className="flex items-center gap-4">
          <button className="p-2 text-white hover:bg-white/10 rounded-lg transition-colors">
            <Bell className="h-5 w-5" />
          </button>

          <div className="flex items-center gap-3 px-4 py-2 bg-white/10 rounded-xl backdrop-blur-sm">
            <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full flex items-center justify-center">
              <User className="h-4 w-4 text-white" />
            </div>
            <span className="text-sm font-medium">{user}</span>
          </div>

          <LogoutButton />
        </div>
      </div>
    </header>
  );
}
