import { useEffect, useState } from 'react';

export default function DoctorDashboard() {
  const [patients, setPatients] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [disease, setDisease] = useState('');
  const [toast, setToast] = useState(null);

  // Mock data for testing
  useEffect(() => {
    const mockPatients = [
      { id: 1, name: 'John Doe', age: 35, disease: 'Hypertension' },
      { id: 2, name: 'Jane Smith', age: 28, disease: 'Diabetes' },
      { id: 3, name: 'Bob Johnson', age: 45, disease: 'Asthma' },
    ];
    setPatients(mockPatients);
  }, []);

  const handleEdit = (patient) => {
    setEditingId(patient.id);
    setName(patient.name);
    setAge(String(patient.age));
    setDisease(patient.disease);
  };

  const handleUpdate = async (e) => {
    e.preventDefault();

    const updatedPatient = {
      id: editingId,
      name,
      age: parseInt(age),
      disease
    };

    setPatients(patients.map(p => p.id === editingId ? updatedPatient : p));
    setToast({ message: 'Patient updated successfully!', type: 'success' });
    resetForm();
  };

  const resetForm = () => {
    setEditingId(null);
    setName('');
    setAge('');
    setDisease('');
  };

  return (
    <div>
      <div className="dashboard-header">
        <h1 className="dashboard-title">Doctor Dashboard</h1>
        <p className="dashboard-subtitle">Review and update patient medical records</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card green">
          <div className="stat-info">
            <h3>Total Patients</h3>
            <p>{patients.length}</p>
          </div>
          <div className="stat-icon">üë•</div>
        </div>

        <div className="stat-card blue">
          <div className="stat-info">
            <h3>Active Cases</h3>
            <p>{patients.filter(p => p.disease).length}</p>
          </div>
          <div className="stat-icon">üìã</div>
        </div>

        <div className="stat-card purple">
          <div className="stat-info">
            <h3>Consultations</h3>
            <p>24</p>
          </div>
          <div className="stat-icon">ü©∫</div>
        </div>

        <div className="stat-card orange">
          <div className="stat-info">
            <h3>Reports</h3>
            <p>12</p>
          </div>
          <div className="stat-icon">üìÑ</div>
        </div>
      </div>

      {editingId && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2 className="modal-title">Update Patient Record</h2>
              <p className="modal-subtitle">Modify patient information and medical details</p>
            </div>

            <form onSubmit={handleUpdate}>
              <div className="form-group">
                <label className="form-label">Patient Name</label>
                <input
                  type="text"
                  className="form-input"
                  placeholder="Enter patient name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                  style={{ paddingLeft: '16px' }}
                />
              </div>

              <div className="form-group">
                <label className="form-label">Age</label>
                <input
                  type="number"
                  className="form-input"
                  placeholder="Enter age"
                  value={age}
                  onChange={(e) => setAge(e.target.value)}
                  required
                  style={{ paddingLeft: '16px' }}
                />
              </div>

              <div className="form-group">
                <label className="form-label">Medical Condition/Diagnosis</label>
                <textarea
                  className="form-input"
                  placeholder="Enter diagnosis, symptoms, or medical notes..."
                  value={disease}
                  onChange={(e) => setDisease(e.target.value)}
                  rows="4"
                  style={{ paddingLeft: '16px', minHeight: '100px', resize: 'vertical' }}
                />
              </div>

              <div className="form-actions">
                <button type="button" className="btn btn-cancel" onClick={resetForm}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-submit">
                  Update Record
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div style={{ background: 'white', borderRadius: '16px', padding: '24px', boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)' }}>
        <h2 style={{ fontSize: '24px', fontWeight: '700', color: '#1f2937', marginBottom: '16px', display: 'flex', alignItems: 'center', gap: '8px' }}>
          üìÑ Patient Records
        </h2>
        <p style={{ color: '#6b7280', marginBottom: '24px' }}>Click on any patient to update their medical information</p>

        <div style={{ display: 'flex', flexDirection: 'column', gap: '16px' }}>
          {patients.map((patient) => (
            <div
              key={patient.id}
              onClick={() => handleEdit(patient)}
              style={{
                background: 'white',
                border: '1px solid #e5e7eb',
                borderLeft: '4px solid #10b981',
                borderRadius: '12px',
                padding: '20px',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                boxShadow: '0 2px 8px rgba(0, 0, 0, 0.05)'
              }}
              onMouseEnter={(e) => {
                e.target.style.transform = 'translateY(-2px)';
                e.target.style.boxShadow = '0 8px 25px rgba(0, 0, 0, 0.1)';
              }}
              onMouseLeave={(e) => {
                e.target.style.transform = 'translateY(0)';
                e.target.style.boxShadow = '0 2px 8px rgba(0, 0, 0, 0.05)';
              }}
            >
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                <div style={{ flex: 1 }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '12px' }}>
                    <h3 style={{ fontSize: '20px', fontWeight: '600', color: '#1f2937', margin: 0 }}>{patient.name}</h3>
                    <span style={{ background: '#dcfce7', color: '#166534', padding: '4px 12px', borderRadius: '20px', fontSize: '12px', fontWeight: '600' }}>
                      ID: {patient.id}
                    </span>
                  </div>
                  <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '16px', color: '#6b7280' }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                      <span>üë§</span>
                      <span>Age: {patient.age} years</span>
                    </div>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                      <span>üè•</span>
                      <span>Condition: {patient.disease || 'No diagnosis recorded'}</span>
                    </div>
                  </div>
                </div>
                <button
                  style={{
                    background: '#f0fdf4',
                    color: '#166534',
                    border: '1px solid #bbf7d0',
                    padding: '8px 16px',
                    borderRadius: '8px',
                    fontSize: '14px',
                    fontWeight: '600',
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '4px'
                  }}
                >
                  ‚úèÔ∏è Edit
                </button>
              </div>
            </div>
          ))}
        </div>

        {patients.length === 0 && (
          <div className="empty-state">
            <div className="empty-icon">ü©∫</div>
            <h3 className="empty-title">No Patient Records</h3>
            <p className="empty-subtitle">Patient records will appear here once they are registered.</p>
          </div>
        )}
      </div>

      {toast && (
        <div className={`toast ${toast.type === 'success' ? 'toast-success' : 'toast-error'}`}>
          <span className="toast-icon">{toast.type === 'success' ? '‚úÖ' : '‚ùå'}</span>
          <span className="toast-message">{toast.message}</span>
          <button className="toast-close" onClick={() => setToast(null)}>
            √ó
          </button>
        </div>
      )}
    </div>
  );
}
