        <body>
          <div class="header">
            <h1>📝 Medical Record - ${patient.name}</h1>
            <button class="cancel-btn" onclick="window.close()">❌ Close</button>
          </div>
          <div class="container">

            <div class="two-columns">
              <!-- Left Column -->
              <div>
                <div class="form-group">
                  <h2>👤 Patient Information</h2>
                  <p><strong>Name:</strong> ${patient.name}</p>
                  <p><strong>Age:</strong> ${patient.age} years</p>
                  <p><strong>Gender:</strong> ${patient.gender || 'Not specified'}</p>
                  <p><strong>Phone:</strong> ${patient.phone_number || 'Not provided'}</p>
                  <p><strong>Relationship:</strong> ${patient.relationship || 'Self'}</p>
                  <p><strong>Patient ID:</strong> ${patient.id}</p>
                </div>

                <div class="form-group">
                  <h2>🩺 Medical Visit History</h2>
                  <div id="visitHistory">
                    ${allHistory && allHistory.length > 0 ? allHistory.map((visit, index) => {
                          const isCurrentRecord = visit.id === patient.id || index === allHistory.length - 1;
                          return `<div class="visit-history ${isCurrentRecord ? 'current-record' : ''}">
                            <p><strong>📅 Date:</strong> ${visit.updated_at ? new Date(visit.updated_at).toLocaleDateString() : 'Not specified'}</p>
                            <p><strong>🏥 Diagnosis:</strong> ${visit.diagnosis || 'None'}</p>
                            <p><strong>📝 Notes:</strong> ${visit.medical_notes || 'None'}</p>
                            <p><strong>💊 Prescriptions:</strong> ${visit.prescriptions || 'None'}</p>
                            <p><strong>📅 Next Appointment:</strong> ${visit.next_appointment || 'Not scheduled'}</p>
                            ${isCurrentRecord ? '<p><em>🟢 Current Record</em></p>' : ''}
                          </div>`;
                        }).join('') : `<div class="visit-history">
                          <p><strong>📅 Current Visit</strong></p>
                          <p><strong>🏥 Diagnosis:</strong> ${patient.diagnosis || 'None'}</p>
                          <p><strong>📝 Notes:</strong> ${patient.medical_notes || 'None'}</p>
                          <p><strong>💊 Prescriptions:</strong> ${patient.prescriptions || 'None'}</p>
                          <p><strong>📅 Last Checkup:</strong> ${patient.last_checkup || 'Not specified'}</p>
                          <p><em>🟢 This is the first/current record</em></p>
                        </div>`}
                  </div>
                </div>

                <div class="form-group">
                  <h2>👨‍👩‍👧‍👦 Family Members</h2>
                  <div>
                    ${familyData.family_members && familyData.family_members.length > 0 ? familyData.family_members.map(member => `<p>👤 <strong>${member.name}</strong> (${member.relationship || 'Unknown'}) - Age: ${member.age}</p>`).join('') : '<p>No other family members found using this phone number.</p>'}
                  </div>
                </div>
              </div>

              <!-- Right Column -->
              <div>
                <div class="form-group">
                  <h2>🏥 Current Diagnosis</h2>
                  <input type="text" id="diagnosis" value="${patient.diagnosis || ''}" />
                </div>

                <div class="form-group">
                  <h2>📝 Medical Notes</h2>
                  <textarea id="medical_notes">${patient.medical_notes || ''}</textarea>
                </div>

                <div class="form-group">
                  <h2>💊 Prescriptions</h2>
                  <textarea id="prescriptions">${patient.prescriptions || ''}</textarea>
                </div>

                <div class="form-group">
                  <h2>📅 Appointment Information</h2>
                  <label>Next Checkup Date:</label>
                  <input type="date" id="next_appointment" value="${patient.next_appointment || ''}" />
                </div>

                <div class="buttons">
                  <button type="button" class="save-btn" onclick="saveRecord()">💾 Save Record</button>
                  <button type="button" class="cancel-btn" onclick="window.close()">❌ Cancel</button>
                </div>
              </div>
            </div>
          </div>

