"use client"

import { useEffect, useState } from "react"

const ReceptionistDashboard = () => {
  const [patients, setPatients] = useState([])
  const [showForm, setShowForm] = useState(false)
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    gender: "",
    diagnosis: "",
    phone_number: "",
    relationship: "self",
  })
  const [editingPatientId, setEditingPatientId] = useState(null)
  const [loading, setLoading] = useState(true)

  const fetchPatients = async () => {
    try {
      const token = localStorage.getItem("token")
      const res = await fetch("http://localhost:8080/api/patients", {
        headers: { Authorization: `Bearer ${token}` },
      })

      if (res.ok) {
        const data = await res.json()
        setPatients(data || [])
      } else {
        console.error("Failed to fetch patients")
      }
    } catch (error) {
      console.error("Error fetching patients:", error)
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchPatients()
  }, [])

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!formData.name.trim() || !formData.age.trim()) {
      alert("Name and Age are required!")
      return
    }

    const patientData = {
      name: formData.name.trim(),
      age: Number(formData.age),
      gender: formData.gender,
      diagnosis: formData.diagnosis.trim() || "",
      phone_number: formData.phone_number.trim(),
      relationship: formData.relationship,
    }

    try {
      const token = localStorage.getItem("token")
      const url = editingPatientId
        ? `http://localhost:8080/api/patients/${editingPatientId}`
        : "http://localhost:8080/api/patients"

      const method = editingPatientId ? "PUT" : "POST"

      const res = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(patientData),
      })

      if (res.ok) {
        alert(editingPatientId ? "✅ Patient updated" : "✅ Patient added")
        await fetchPatients()
        resetForm()
      } else {
        const errorData = await res.json()
        alert(`❌ Failed to save patient: ${errorData.error}`)
      }
    } catch (error) {
      alert("❌ Network error")
    }
  }

  const resetForm = () => {
    setShowForm(false)
    setEditingPatientId(null)
    setFormData({
      name: "",
      age: "",
      gender: "",
      diagnosis: "",
      phone_number: "",
      relationship: "self",
    })
  }

  const handleEdit = (patient) => {
    setEditingPatientId(patient.id)
    setFormData({
      name: patient.name || "",
      age: String(patient.age) || "",
      gender: patient.gender || "",
      diagnosis: patient.diagnosis || "",
      phone_number: patient.phone_number || "",
      relationship: patient.relationship || "self",
    })
    setShowForm(true)
  }

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this patient?")) return

    try {
      const token = localStorage.getItem("token")
      const res = await fetch(`http://localhost:8080/api/patients/${id}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })

      if (res.ok) {
        alert("🗑️ Patient deleted")
        await fetchPatients()
      } else {
        alert("❌ Failed to delete patient")
      }
    } catch (error) {
      alert("❌ Network error")
    }
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-6 rounded-lg">
        <h1 className="text-3xl font-bold mb-2">👩‍💼 Receptionist Dashboard</h1>
        <p className="text-blue-100">Manage patient registrations and appointments</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-white p-6 rounded-lg shadow-md border-l-4 border-blue-500">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-600 text-sm">Total Patients</p>
              <p className="text-3xl font-bold text-gray-900">{patients.length}</p>
            </div>
            <div className="text-4xl text-blue-500">👥</div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-md border-l-4 border-green-500">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-600 text-sm">New Today</p>
              <p className="text-3xl font-bold text-gray-900">
                {
                  patients.filter((p) => {
                    const today = new Date().toDateString()
                    return new Date(p.created_at).toDateString() === today
                  }).length
                }
              </p>
            </div>
            <div className="text-4xl text-green-500">📅</div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-md border-l-4 border-purple-500">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-600 text-sm">Active Cases</p>
              <p className="text-3xl font-bold text-gray-900">
                {patients.filter((p) => p.diagnosis && p.diagnosis.trim() !== "").length}
              </p>
            </div>
            <div className="text-4xl text-purple-500">🏥</div>
          </div>
        </div>
      </div>

      {/* Add Patient Button */}
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">Patient Management</h2>
        <button
          onClick={() => {
            setShowForm(!showForm)
            if (showForm) resetForm()
          }}
          className="bg-blue-600 text-white px-6 py-3 rounded-lg font-medium hover:bg-blue-700 transition-colors flex items-center gap-2"
        >
          <span>{showForm ? "❌" : "➕"}</span>
          {showForm ? "Cancel" : "Add Patient"}
        </button>
      </div>

      {/* Add/Edit Patient Form */}
      {showForm && (
        <div className="bg-white p-6 rounded-lg shadow-md border">
          <h3 className="text-xl font-semibold mb-4 text-gray-900">
            {editingPatientId ? "✏️ Edit Patient" : "➕ Add New Patient"}
          </h3>
          <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Name *</label>
              <input
                type="text"
                required
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="Enter patient name"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Age *</label>
              <input
                type="number"
                required
                value={formData.age}
                onChange={(e) => setFormData({ ...formData, age: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="Enter age"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Gender</label>
              <select
                value={formData.gender}
                onChange={(e) => setFormData({ ...formData, gender: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="">Select Gender</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Other">Other</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Phone Number</label>
              <input
                type="tel"
                value={formData.phone_number}
                onChange={(e) => setFormData({ ...formData, phone_number: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="Enter phone number"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Relationship</label>
              <select
                value={formData.relationship}
                onChange={(e) => setFormData({ ...formData, relationship: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="self">Self</option>
                <option value="father">Father</option>
                <option value="mother">Mother</option>
                <option value="son">Son</option>
                <option value="daughter">Daughter</option>
                <option value="spouse">Spouse</option>
                <option value="sibling">Sibling</option>
                <option value="other">Other</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Initial Diagnosis</label>
              <input
                type="text"
                value={formData.diagnosis}
                onChange={(e) => setFormData({ ...formData, diagnosis: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="Enter initial diagnosis (optional)"
              />
            </div>

            <div className="md:col-span-2 flex space-x-3">
              <button
                type="submit"
                className="bg-green-600 text-white px-6 py-2 rounded-lg font-medium hover:bg-green-700 transition-colors flex items-center gap-2"
              >
                <span>💾</span>
                {editingPatientId ? "Update" : "Add"} Patient
              </button>
              <button
                type="button"
                onClick={resetForm}
                className="bg-gray-500 text-white px-6 py-2 rounded-lg font-medium hover:bg-gray-600 transition-colors"
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Patients List */}
      <div className="bg-white rounded-lg shadow-md">
        <div className="px-6 py-4 border-b border-gray-200">
          <h3 className="text-xl font-semibold text-gray-900">📋 Patients List</h3>
        </div>

        {loading ? (
          <div className="p-6 text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500 mx-auto"></div>
            <p className="mt-2 text-gray-600">Loading patients...</p>
          </div>
        ) : patients.length === 0 ? (
          <div className="p-6 text-center text-gray-500">
            <div className="text-4xl mb-2">👥</div>
            <p>No patients found. Add your first patient!</p>
          </div>
        ) : (
          <div className="divide-y divide-gray-200">
            {patients.map((patient) => (
              <div key={patient.id} className="p-6 hover:bg-gray-50 transition-colors">
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-2">
                      <h4 className="text-lg font-medium text-gray-900">{patient.name}</h4>
                      <span className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded-full">ID: {patient.id}</span>
                      {patient.relationship && (
                        <span className="bg-purple-100 text-purple-800 text-xs px-2 py-1 rounded-full capitalize">
                          {patient.relationship}
                        </span>
                      )}
                    </div>
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm text-gray-600">
                      <div>
                        <span className="font-medium">Age:</span> {patient.age} years
                      </div>
                      <div>
                        <span className="font-medium">Gender:</span> {patient.gender || "N/A"}
                      </div>
                      <div>
                        <span className="font-medium">Phone:</span> {patient.phone_number || "N/A"}
                      </div>
                      <div>
                        <span className="font-medium">Diagnosis:</span> {patient.diagnosis || "N/A"}
                      </div>
                    </div>
                  </div>
                  <div className="flex space-x-2 ml-4">
                    <button
                      onClick={() => handleEdit(patient)}
                      className="bg-yellow-500 text-white px-3 py-1 rounded text-sm font-medium hover:bg-yellow-600 transition-colors"
                    >
                      ✏️ Edit
                    </button>
                    <button
                      onClick={() => handleDelete(patient.id)}
                      className="bg-red-600 text-white px-3 py-1 rounded text-sm font-medium hover:bg-red-700 transition-colors"
                    >
                      🗑️ Delete
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  )
}

export default ReceptionistDashboard
